import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { postAPI } from '../services/api';

interface StudyPost {
  id: number;
  title: string;
  content: string;
  studyStatus: string;
  currentNumber: number;
  recruitNumber: number;
  author: {
    id: number;
    name: string;
  };
  createdAt: string;
}

const MyCreatedStudies: React.FC = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [studies, setStudies] = useState<StudyPost[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (user) {
      fetchMyCreatedStudies();
    }
  }, [user]);

  const fetchMyCreatedStudies = async () => {
    try {
      const response = await postAPI.getMyStudies();
      console.log('üîç fetchMyCreatedStudies - response:', response);
      console.log('üîç fetchMyCreatedStudies - response.data:', response.data);
      console.log('üîç fetchMyCreatedStudies - response.data.data:', response.data?.data);
      console.log('üîç fetchMyCreatedStudies - current user id:', user?.id);

      let studiesData = [];

      // API ÏùëÎãµ Íµ¨Ï°∞Î•º Ï†ïÌôïÌûà ÌååÏïÖ
      if (response.data?.data?.data && Array.isArray(response.data.data.data)) {
        // response.data.data.data Íµ¨Ï°∞
        studiesData = response.data.data.data;
        console.log('üîç Using response.data.data.data structure');
      } else if (response.data?.data && Array.isArray(response.data.data)) {
        // response.data.data Íµ¨Ï°∞
        studiesData = response.data.data;
        console.log('üîç Using response.data.data structure');
      } else if (response.data && Array.isArray(response.data)) {
        // response.data Íµ¨Ï°∞
        studiesData = response.data;
        console.log('üîç Using response.data structure');
      } else if (Array.isArray(response)) {
        // response ÏûêÏ≤¥Í∞Ä Î∞∞Ïó¥
        studiesData = response;
        console.log('üîç Using response directly');
