const StudyList = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [studies, setStudies] = useState<StudySession[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchStudies();
  }, []);

  const fetchStudies = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch("http://localhost:3001/api/post", {
        headers: { Authorization: `Bearer ${token}` },
      });
      
      if (response.ok) {
        const data = await response.json();
        console.log("Studies API Response:", data); // Debug log
        // Handle different possible response structures
        if (Array.isArray(data)) {
          setStudies(data);
        } else if (data && Array.isArray(data.data)) {
          setStudies(data.data);
        } else if (data && Array.isArray(data.posts)) {
          setStudies(data.posts);
        } else {
          console.warn("Unexpected studies response structure:", data);
          setStudies([]);
        }
      } else {
        console.error("Failed to fetch studies:", response.status, response.statusText);
        setStudies([]);
      }
    } catch (error) {
      console.error("Failed to fetch studies:", error);
      setStudies([]);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-gray-900">스터디 목록</h1>
          <button
            onClick={() => navigate("/create-study")}
            className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700"
          >
            스터디 만들기
          </button>
        </div>

        {!Array.isArray(studies) || studies.length === 0 ? (
          <div className="text-center py-12">
            <p className="text-gray-500 text-lg">등록된 스터디가 없습니다.</p>
            <button
              onClick={() => navigate("/create-study")}
              className="mt-4 bg-indigo-600 text-white px-6 py-3 rounded-md hover:bg-indigo-700"
            >
              첫 번째 스터디를 만들어보세요!
            </button>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {studies.map((study) => (
              <div
                key={study.id}
                className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow cursor-pointer"
                onClick={() => navigate(`/studies/${study.id}`)}
              >
                <div className="p-6">
                  <h3 className="text-xl font-semibold text-gray-900 mb-2">
                    {study.title}
                  </h3>
                  <p className="text-gray-600 text-sm mb-4 line-clamp-3">
                    {study.description}
                  </p>
                  <div className="flex justify-between items-center text-sm text-gray-500">
                    <span>작성자: {study.author?.username}</span>
                    <span>{study.currentNumber}/{study.recruitNumber}명</span>
                  </div>
                  <div className="mt-4 flex justify-between items-center">
                    <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                      study.studyStatus === 'recruiting' ? 'bg-green-100 text-green-800' :
                      study.studyStatus === 'started' ? 'bg-blue-100 text-blue-800' :
                      study.studyStatus === 'finished' ? 'bg-gray-100 text-gray-800' :
                      'bg-yellow-100 text-yellow-800'
                    }`}>
                      {study.studyStatus === 'recruiting' ? '모집중' :
                       study.studyStatus === 'started' ? '진행중' :
                       study.studyStatus === 'finished' ? '완료' : '대기중'}
                    </span>
                    <span className="text-xs text-gray-500">
                      {new Date(study.createdAt).toLocaleDateString()}
                    </span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

const CreateStudy = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    recruitNumber: 2,
    studyStatus: "recruiting",
    category: "기타",
    mode: "온라인"
  });
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
      const token = localStorage.getItem("token");
      const response = await fetch("http://localhost:3001/api/post", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        toast.success("스터디가 성공적으로 생성되었습니다!");
        navigate("/studies");
      } else {
        const data = await response.json();
        toast.error(data.message || "스터디 생성에 실패했습니다.");
      }
    } catch (error) {
      console.error("Failed to create study:", error);
      toast.error("스터디 생성 중 오류가 발생했습니다.");
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'recruitNumber' ? parseInt(value) : value
    }));
  };

  return (
    <div className="max-w-4xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        <div className="bg-white shadow rounded-lg">
          <div className="px-4 py-5 sm:p-6">
            <h1 className="text-2xl font-bold text-gray-900 mb-6">새 스터디 만들기</h1>
            
            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <label htmlFor="title" className="block text-sm font-medium text-gray-700">
                  스터디 제목 *
                </label>
                <input
                  type="text"
                  id="title"
                  name="title"
                  required
                  value={formData.title}
                  onChange={handleChange}
                  className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  placeholder="스터디 제목을 입력하세요"
                />
              </div>

              <div>
                <label htmlFor="description" className="block text-sm font-medium text-gray-700">
                  스터디 설명 *
                </label>
                <textarea
                  id="description"
                  name="description"
                  required
                  rows={6}
                  value={formData.description}
                  onChange={handleChange}
                  className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  placeholder="스터디에 대한 자세한 설명을 입력하세요"
                />
              </div>

              <div>
                <label htmlFor="recruitNumber" className="block text-sm font-medium text-gray-700">
                  최대 인원 *
                </label>
                <select
                  id="recruitNumber"
                  name="recruitNumber"
                  value={formData.recruitNumber}
                  onChange={handleChange}
                  className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
                >
                  {[2, 3, 4, 5, 6, 7, 8, 9, 10].map(num => (
                    <option key={num} value={num}>{num}명</option>
                  ))}
                </select>
              </div>

              <div>
                <label htmlFor="category" className="block text-sm font-medium text-gray-700">
                  카테고리 *
                </label>
                <select
                  id="category"
                  name="category"
                  value={formData.category}
                  onChange={handleChange}
                  className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
                >
                  <option value="기타">기타</option>
                  <option value="프로그래밍">프로그래밍</option>
                  <option value="언어">언어</option>
                  <option value="자격증">자격증</option>
                  <option value="취업준비">취업준비</option>
                  <option value="시험준비">시험준비</option>
                </select>
              </div>

              <div>
                <label htmlFor="mode" className="block text-sm font-medium text-gray-700">
                  진행 방식 *
                </label>
                <select
                  id="mode"
                  name="mode"
                  value={formData.mode}
                  onChange={handleChange}
                  className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
                >
                  <option value="온라인">온라인</option>
                  <option value="오프라인">오프라인</option>
                  <option value="혼합">혼합</option>
                </select>
              </div>

              <div className="flex justify-end space-x-3">
                <button
                  type="button"
                  onClick={() => navigate("/studies")}
                  className="px-4 py-2 text-gray-600 border border-gray-300 rounded-md hover:bg-gray-50"
                >
                  취소
                </button>
                <button
                  type="submit"
                  disabled={loading}
                  className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 disabled:opacity-50"
                >
                  {loading ? "생성 중..." : "스터디 만들기"}
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

const MyStudies = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [myStudies, setMyStudies] = useState<StudySession[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchMyStudies();
  }, []);

  const fetchMyStudies = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch("http://localhost:3001/api/post/user/my-studies", {
        headers: { Authorization: `Bearer ${token}` },
      });
      
      if (response.ok) {
        const data = await response.json();
        console.log("MyStudies API Response:", data); // Debug log
        // Handle different possible response structures
        if (Array.isArray(data)) {
          setMyStudies(data);
        } else if (data && Array.isArray(data.data)) {
          setMyStudies(data.data);
        } else if (data && Array.isArray(data.studies)) {
          setMyStudies(data.studies);
        } else {
          console.warn("Unexpected my studies response structure:", data);
          setMyStudies([]);
        }
      } else {
        console.error("Failed to fetch my studies:", response.status, response.statusText);
        setMyStudies([]);
      }
    } catch (error) {
      console.error("Failed to fetch my studies:", error);
      setMyStudies([]);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-gray-900">내 스터디</h1>
          <button
            onClick={() => navigate("/create-study")}
            className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700"
          >
            새 스터디 만들기
          </button>
        </div>

        {!Array.isArray(myStudies) || myStudies.length === 0 ? (
          <div className="text-center py-12">
            <p className="text-gray-500 text-lg">참여 중인 스터디가 없습니다.</p>
            <button
              onClick={() => navigate("/studies")}
              className="mt-4 bg-indigo-600 text-white px-6 py-3 rounded-md hover:bg-indigo-700"
            >
              스터디 찾아보기
            </button>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {myStudies.map((study) => (
              <div
                key={study.id}
                className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow cursor-pointer"
                onClick={() => navigate(`/studies/${study.id}`)}
              >
                <div className="p-6">
                  <h3 className="text-xl font-semibold text-gray-900 mb-2">
                    {study.title}
                  </h3>
                  <p className="text-gray-600 text-sm mb-4 line-clamp-3">
                    {study.description}
                  </p>
                  <div className="flex justify-between items-center text-sm text-gray-500">
                    <span>작성자: {study.author?.username}</span>
                    <span>{study.currentNumber}/{study.recruitNumber}명</span>
                  </div>
                  <div className="mt-4 flex justify-between items-center">
                    <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                      study.studyStatus === 'recruiting' ? 'bg-green-100 text-green-800' :
                      study.studyStatus === 'started' ? 'bg-blue-100 text-blue-800' :
                      'bg-gray-100 text-gray-800'
                    }`}>
                      {study.studyStatus === 'recruiting' ? '모집중' :
                       study.studyStatus === 'started' ? '진행중' :
                       study.studyStatus === 'finished' ? '완료' : '대기중'}
                    </span>
                    <span className="text-xs text-gray-500">
                      {new Date(study.createdAt).toLocaleDateString()}
                    </span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

const MyApplications = () => {
