import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { useAuth } from './contexts/AuthContext';
import Header from './components/Layout/Header';
import Home from './pages/Home';
import LoginForm from './components/Auth/LoginForm';
import './index.css';

// 보호된 라우트 컴포넌트
const ProtectedRoute: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const { user, loading } = useAuth();

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  if (!user) {
    return <Navigate to="/login" replace />;
  }

  return <>{children}</>;
};

// 메인 레이아웃 컴포넌트
const Layout: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const { user } = useAuth();

  return (
    <div className="min-h-screen bg-gray-50">
      {user && <Header />}
      <main>{children}</main>
    </div>
  );
};

const App: React.FC = () => {
  return (
    <Router>
      <Layout>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<LoginForm />} />
          <Route path="/register" element={<div>회원가입 페이지 (추후 구현)</div>} />
          <Route path="/studies" element={<div>스터디 목록 페이지 (추후 구현)</div>} />
          <Route path="/studies/:id" element={<div>스터디 상세 페이지 (추후 구현)</div>} />
          <Route path="/create-study" element={<div>스터디 생성 페이지 (추후 구현)</div>} />
          <Route path="/my-studies" element={<div>내 스터디 페이지 (추후 구현)</div>} />
          <Route path="/profile" element={<div>프로필 페이지 (추후 구현)</div>} />
          <Route path="/settings" element={<div>설정 페이지 (추후 구현)</div>} />
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </Layout>
    </Router>
  );
};

export default App;
