        // serverFileNameÏù¥ ÏûàÏúºÎ©¥ ÏÇ¨Ïö©
        if (fileItem.serverFileName) {
          const response = await fetch(`/api/upload/files/${fileItem.serverFileName}`, {
            headers: {
              'Authorization': `Bearer ${localStorage.getItem('token')}`,
            },
          });

          if (response.ok) {
            const blob = await response.blob();
            const url = window.URL.createObjectURL(blob);
            const a = document.createElement('a');
            a.href = url;
            a.download = fileItem.fileName || fileItem.serverFileName;
            document.body.appendChild(a);
            a.click();
            window.URL.revokeObjectURL(url);
            document.body.removeChild(a);
          } else {
            alert('ÌååÏùº Îã§Ïö¥Î°úÎìúÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
          }
        } else if (fileItem.url && !fileItem.url.startsWith('blob:')) {
          // blob URLÏù¥ ÏïÑÎãå Í≤ΩÏö∞ ÏßÅÏ†ë Îã§Ïö¥Î°úÎìú
          const a = document.createElement('a');
          a.href = fileItem.url;
          a.download = fileItem.fileName || 'ÌååÏùº';
          a.target = '_blank';
          document.body.appendChild(a);
          a.click();
          document.body.removeChild(a);
        } else {
          alert('Ïù¥ ÌååÏùºÏùÄ Îã§Ïö¥Î°úÎìúÌï† Ïàò ÏóÜÏäµÎãàÎã§. ÌååÏùºÏù¥ ÏÑúÎ≤ÑÏóê Ï†úÎåÄÎ°ú ÏóÖÎ°úÎìúÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§.');
        }
