  // Ï∂úÏÑù Ï≤¥ÌÅ¨ ÏãúÍ∞Ñ Ï†úÌïú ÌôïÏù∏ Ìï®Ïàò
  const isAttendanceTimeValid = (session: StudySession) => {
    if (!session.scheduledDate || !session.startHour) return false;

    const now = new Date();
    const sessionStart = new Date(`${session.scheduledDate}T${session.startHour}`);

    // Ïä§ÌÑ∞Îîî ÏãúÏûë 10Î∂Ñ Ï†ÑÎ∂ÄÌÑ∞ 20Î∂Ñ Ïù¥ÎÇ¥
    const validStart = new Date(sessionStart.getTime() - 10 * 60 * 1000); // 10Î∂Ñ Ï†Ñ
    const validEnd = new Date(sessionStart.getTime() + 20 * 60 * 1000);   // 20Î∂Ñ ÌõÑ

    return now >= validStart && now <= validEnd;
  };

  // Ï∂úÏÑù Ï≤¥ÌÅ¨ Í∞ÄÎä• ÏãúÍ∞ÑÍπåÏßÄ ÎÇ®ÏùÄ ÏãúÍ∞Ñ Í≥ÑÏÇ∞
  const getTimeUntilAttendance = (session: StudySession) => {
    if (!session.scheduledDate || !session.startHour) return null;

    const now = new Date();
    const sessionStart = new Date(`${session.scheduledDate}T${session.startHour}`);
    const validStart = new Date(sessionStart.getTime() - 10 * 60 * 1000);

    if (now < validStart) {
      const diff = validStart.getTime() - now.getTime();
      const minutes = Math.floor(diff / (1000 * 60));
      const seconds = Math.floor((diff % (1000 * 60)) / 1000);
      return `${minutes}Î∂Ñ ${seconds}Ï¥à ÌõÑ Ï∂úÏÑù Í∞ÄÎä•`;
    }

    return null;
  };

  // Firebase Ìë∏Ïãú ÏïåÎ¶º Ï†ÑÏÜ° Ìï®Ïàò
  const sendFirebaseNotification = async (session: StudySession) => {
    try {
      // Ïä§ÌÑ∞Îîî Î©§Î≤Ñ Ï†ÑÏ≤¥ÏóêÍ≤å ÏïåÎ¶ºÏùÑ Î≥¥ÎÇ¥Îäî ÏóîÎìúÌè¨Ïù∏Ìä∏ ÏÇ¨Ïö©
      const response = await fetch('/api/firebase/send-study-notification', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
        },
        body: JSON.stringify({
          sessionId: session.id,
          title: 'Ïä§ÌÑ∞Îîî ÏïåÎ¶º',
          body: `${session.title} ÏÑ∏ÏÖòÏù¥ 10Î∂Ñ ÌõÑÏóê ÏãúÏûëÎê©ÎãàÎã§!`,
          data: {
            sessionId: session.id,
            sessionTitle: session.title,
            type: 'study_reminder',
            scheduledDate: session.scheduledDate,
            startHour: session.startHour
          }
        }),
      });

      if (response.ok) {
        console.log('üî• Firebase ÏïåÎ¶º Ï†ÑÏÜ° ÏÑ±Í≥µ');
      } else {
        console.error('üî• Firebase ÏïåÎ¶º Ï†ÑÏÜ° Ïã§Ìå®:', response.status);
      }
    } catch (error) {
      console.error('üî• Firebase ÏïåÎ¶º Ï†ÑÏÜ° Ïò§Î•ò:', error);
    }
  };

  // Ïä§ÌÑ∞Îîî ÏïåÎûå ÏÑ§Ï†ï Ìï®Ïàò
  const setStudyAlarm = (session: StudySession) => {
    if (!session.scheduledDate || !session.startHour) return;

    const sessionStart = new Date(`${session.scheduledDate}T${session.startHour}`);
    const alarmTime = new Date(sessionStart.getTime() - 10 * 60 * 1000); // 10Î∂Ñ Ï†Ñ ÏïåÎûå

    // ÌòÑÏû¨ ÏãúÍ∞ÑÎ≥¥Îã§ ÎØ∏ÎûòÏù∏ Í≤ΩÏö∞ÏóêÎßå ÏïåÎûå ÏÑ§Ï†ï
    if (alarmTime > new Date()) {
      const timeUntilAlarm = alarmTime.getTime() - new Date().getTime();

      setTimeout(() => {
        // Firebase Ìë∏Ïãú ÏïåÎ¶º Ï†ÑÏÜ°
        sendFirebaseNotification(session);

        // Î∏åÎùºÏö∞Ï†Ä ÏïåÎ¶º Í∂åÌïú ÌôïÏù∏
        if (Notification.permission === 'granted') {
          new Notification('Ïä§ÌÑ∞Îîî ÏïåÎ¶º', {
            body: `${session.title} ÏÑ∏ÏÖòÏù¥ 10Î∂Ñ ÌõÑÏóê ÏãúÏûëÎê©ÎãàÎã§!`,
            icon: '/favicon.ico',
            tag: `study-${session.id}`,
          });
        } else if (Notification.permission !== 'denied') {
          // Í∂åÌïú ÏöîÏ≤≠
          Notification.requestPermission().then(permission => {
            if (permission === 'granted') {
              new Notification('Ïä§ÌÑ∞Îîî ÏïåÎ¶º', {
                body: `${session.title} ÏÑ∏ÏÖòÏù¥ 10Î∂Ñ ÌõÑÏóê ÏãúÏûëÎê©ÎãàÎã§!`,
                icon: '/favicon.ico',
                tag: `study-${session.id}`,
              });
            }
          });
        }

        // Ï∂îÍ∞ÄÎ°ú ÌéòÏù¥ÏßÄ ÎÇ¥ ÏïåÎ¶º ÌëúÏãú
        showPageNotification(`${session.title} ÏÑ∏ÏÖòÏù¥ 10Î∂Ñ ÌõÑÏóê ÏãúÏûëÎê©ÎãàÎã§!`);
      }, timeUntilAlarm);

      console.log(`üîî ÏïåÎûå ÏÑ§Ï†ï ÏôÑÎ£å: ${session.title} - ${alarmTime.toLocaleString()}`);
    }
  };

  // ÌéòÏù¥ÏßÄ ÎÇ¥ ÏïåÎ¶º ÌëúÏãú Ìï®Ïàò
  const showPageNotification = (message: string) => {
    // Í∏∞Ï°¥ ÏïåÎ¶º Ï†úÍ±∞
    const existingNotification = document.getElementById('page-notification');
    if (existingNotification) {
      existingNotification.remove();
    }

    // ÏÉà ÏïåÎ¶º ÏÉùÏÑ±
    const notification = document.createElement('div');
    notification.id = 'page-notification';
    notification.className = 'fixed top-4 right-4 bg-blue-600 text-white px-6 py-4 rounded-lg shadow-lg z-50 max-w-sm';
    notification.innerHTML = `
      <div class="flex items-center">
        <div class="flex-shrink-0">
