import React, { useState, useEffect, createContext, useContext } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link, useNavigate } from 'react-router-dom';
import { QueryClient, QueryClientProvider } from 'react-query';
import { Toaster, toast } from 'react-hot-toast';

const queryClient = new QueryClient();

const AuthContext = createContext();

const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) throw new Error('useAuth must be used within an AuthProvider');
  return context;
};

const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    console.log('useEffect - Token found:', !!token);
    if (token) {
      console.log('useEffect - Calling fetchCurrentUser');
      fetchCurrentUser();
    }
  }, []);

  const fetchCurrentUser = async () => {
    try {
      const token = localStorage.getItem('token');
      console.log('Token from localStorage:', token);
      console.log('Token type:', typeof token);
      console.log('Token length:', token ? token.length : 0);
      
      if (!token) {
        console.log('No token found in localStorage');
        return;
      }
      
      const response = await fetch('http://localhost:3001/api/user/me', {
        headers: { 'Authorization': `Bearer ${token}` },
      });
      console.log('User fetch response status:', response.status);
             if (response.ok) {
         const data = await response.json();
         console.log('User data received:', data);
         console.log('Setting user with data.data:', data.data);
         console.log('data.data.username:', data.data?.username);
         setUser(data.data);
      } else {
        console.log('User fetch failed, removing token');
        localStorage.removeItem('token');
      }
    } catch (error) {
      console.error('Failed to fetch user:', error);
      localStorage.removeItem('token');
    }
  };

  const login = async (email, password) => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:3001/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });
      const data = await response.json();
      console.log('Login response:', data);
      console.log('Response status:', response.status);
      console.log('Response ok:', response.ok);
      console.log('Data keys:', Object.keys(data));
      if (response.ok) {
        // 응답 구조에 따라 토큰 추출
        const token = data.token || (data.data && data.data.token);
        console.log('Token received:', token);
        console.log('Token type:', typeof token);
        console.log('Token length:', token ? token.length : 0);
        
        if (!token) {
          console.error('No token found in response:', data);
          toast.error('토큰을 받지 못했습니다.');
          return;
        }
        
        localStorage.setItem('token', token);
        console.log('Token stored in localStorage');
        
        // 저장된 토큰 확인
        const storedToken = localStorage.getItem('token');
        console.log('Stored token verification:', storedToken);
        
        await fetchCurrentUser();
        toast.success('로그인 성공!');
        navigate('/');
      } else {
        toast.error(data.message || '로그인 실패');
      }
    } catch (error) {
      console.error('Login error:', error);
      toast.error('로그인 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  const register = async (email, username, password) => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:3001/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, username, password }),
      });
      const data = await response.json();
      if (response.ok) {
        toast.success('회원가입 성공! 로그인해주세요.');
        navigate('/login');
      } else {
        toast.error(data.message || '회원가입 실패');
      }
    } catch (error) {
      toast.error('회원가입 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  const logout = () => {
    localStorage.removeItem('token');
    setUser(null);
    toast.success('로그아웃되었습니다.');
    navigate('/login');
  };

  return (
    <AuthContext.Provider value={{ user, loading, login, logout, register, fetchCurrentUser }}>
      {children}
    </AuthContext.Provider>
  );
};

const LoginForm = () => {
  const { login, loading } = useAuth();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    await login(email, password);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div>
          <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">StudyGroup에 로그인</h2>
          <p className="mt-2 text-center text-sm text-gray-600">
            또는{' '}
            <Link to="/register" className="font-medium text-indigo-600 hover:text-indigo-500">
              새 계정 만들기
            </Link>
          </p>
        </div>
        <form className="mt-8 space-y-6" onSubmit={handleSubmit}>
          <div className="rounded-md shadow-sm -space-y-px">
            <div>
              <input
                type="email" required
                className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-t-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                placeholder="이메일 주소"
                value={email} onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div>
              <input
                type="password" required
                className="appearance-none rounded-none relative block w-full px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-b-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm"
                placeholder="비밀번호"
                value={password} onChange={(e) => setPassword(e.target.value)}
              />
            </div>
          </div>

          <button
            type="submit" disabled={loading}
            className="group relative w-full flex justify-center py-2 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50"
          >
            {loading ? '로그인 중...' : '로그인'}
          </button>
        </form>
      </div>
    </div>
  );
};

const RegisterForm = () => {
  const { register, loading } = useAuth();
  const [formData, setFormData] = useState({
    email: '', username: '', password: '', confirmPassword: ''
  });
  const [errors, setErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};
    if (!formData.email) newErrors.email = '이메일을 입력해주세요.';
    if (!formData.username) newErrors.username = '사용자명을 입력해주세요.';
    if (!formData.password) newErrors.password = '비밀번호를 입력해주세요.';
    if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = '비밀번호가 일치하지 않습니다.';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;
    await register(formData.email, formData.username, formData.password);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    if (errors[name]) setErrors({ ...errors, [name]: '' });
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div>
          <h2 className="mt-6 text-center text-3xl font-extrabold text-gray-900">
            StudyGroup 회원가입
          </h2>
          <p className="mt-2 text-center text-sm text-gray-600">
            또는{' '}
            <Link to="/login" className="font-medium text-indigo-600 hover:text-indigo-500">
              기존 계정으로 로그인
            </Link>
          </p>
        </div>
        
        <form className="mt-8 space-y-6" onSubmit={handleSubmit}>
          <div className="space-y-4">
            <div>
              <label htmlFor="email" className="block text-sm font-medium text-gray-700">이메일 주소</label>
              <input
                id="email" name="email" type="email" required
                className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                  errors.email ? 'border-red-500' : 'border-gray-300'
                }`}
                value={formData.email} onChange={handleChange}
              />
              {errors.email && <p className="mt-1 text-sm text-red-600">{errors.email}</p>}
            </div>

            <div>
              <label htmlFor="username" className="block text-sm font-medium text-gray-700">사용자명</label>
              <input
                id="username" name="username" type="text" required
                className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                  errors.username ? 'border-red-500' : 'border-gray-300'
                }`}
                value={formData.username} onChange={handleChange}
              />
              {errors.username && <p className="mt-1 text-sm text-red-600">{errors.username}</p>}
            </div>

            <div>
              <label htmlFor="password" className="block text-sm font-medium text-gray-700">비밀번호</label>
              <input
                id="password" name="password" type="password" required
                className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                  errors.password ? 'border-red-500' : 'border-gray-300'
                }`}
                value={formData.password} onChange={handleChange}
              />
              {errors.password && <p className="mt-1 text-sm text-red-600">{errors.password}</p>}
            </div>

            <div>
              <label htmlFor="confirmPassword" className="block text-sm font-medium text-gray-700">비밀번호 확인</label>
              <input
                id="confirmPassword" name="confirmPassword" type="password" required
                className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                  errors.confirmPassword ? 'border-red-500' : 'border-gray-300'
                }`}
                value={formData.confirmPassword} onChange={handleChange}
              />
              {errors.confirmPassword && <p className="mt-1 text-sm text-red-600">{errors.confirmPassword}</p>}
            </div>
          </div>

          <button
            type="submit" disabled={loading}
            className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50"
          >
            {loading ? '회원가입 중...' : '회원가입'}
          </button>
        </form>
      </div>
    </div>
  );
};

const UserProfile = () => {
  const { user } = useAuth();
  
  return (
    <div className="max-w-4xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        <div className="bg-white overflow-hidden shadow rounded-lg">
          <div className="px-4 py-5 sm:p-6">
            <div className="flex items-center space-x-4 mb-6">
              <div className="h-16 w-16 bg-indigo-600 rounded-full flex items-center justify-center">
                <span className="text-white text-xl font-bold">
                  {user?.username?.charAt(0).toUpperCase()}
                </span>
              </div>
              <div>
                <h1 className="text-2xl font-bold text-gray-900">{user?.username}</h1>
                <p className="text-gray-600">{user?.email}</p>
              </div>
            </div>
            
            <div className="border-t border-gray-200 pt-6">
              <dl className="grid grid-cols-1 gap-x-4 gap-y-6 sm:grid-cols-2">
                <div>
                  <dt className="text-sm font-medium text-gray-500">사용자명</dt>
                  <dd className="mt-1 text-sm text-gray-900">{user?.username}</dd>
                </div>
                <div>
                  <dt className="text-sm font-medium text-gray-500">이메일</dt>
                  <dd className="mt-1 text-sm text-gray-900">{user?.email}</dd>
                </div>
                <div>
                  <dt className="text-sm font-medium text-gray-500">역할</dt>
                  <dd className="mt-1 text-sm text-gray-900">{user?.role || '일반 사용자'}</dd>
                </div>
                <div>
                  <dt className="text-sm font-medium text-gray-500">가입 방식</dt>
                  <dd className="mt-1 text-sm text-gray-900">{user?.provider || '이메일'}</dd>
                </div>
              </dl>
            </div>
            
            <div className="mt-8 flex space-x-4">
              <button className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700">
                프로필 수정
              </button>
              <button className="bg-gray-600 text-white px-4 py-2 rounded-md hover:bg-gray-700">
                비밀번호 변경
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const CreateStudy = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    category: '',
    mode: '',
    location: '',
    recruitNumber: 1,
    studyStartDate: '',
    studyEndDate: ''
  });
  const [errors, setErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};
    if (!formData.title.trim()) newErrors.title = '스터디 제목을 입력해주세요.';
    if (!formData.description.trim()) newErrors.description = '스터디 내용을 입력해주세요.';
    if (!formData.category) newErrors.category = '스터디 분류를 선택해주세요.';
    if (!formData.mode) newErrors.mode = '스터디 모드를 선택해주세요.';
    if (formData.recruitNumber < 1) newErrors.recruitNumber = '모집 인원은 1명 이상이어야 합니다.';
    if (formData.studyStartDate && formData.studyEndDate && 
        new Date(formData.studyStartDate) > new Date(formData.studyEndDate)) {
      newErrors.studyEndDate = '종료일은 시작일보다 늦어야 합니다.';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setLoading(true);
    try {
      const response = await fetch('http://localhost:3001/api/post', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          ...formData,
          recruitNumber: parseInt(formData.recruitNumber),
          studyStartDate: formData.studyStartDate || null,
          studyEndDate: formData.studyEndDate || null
        }),
      });

      const data = await response.json();
      
      if (response.ok) {
        toast.success('스터디가 성공적으로 생성되었습니다!');
        navigate('/studies');
      } else {
        toast.error(data.message || '스터디 생성에 실패했습니다.');
      }
    } catch (error) {
      console.error('Create study error:', error);
      toast.error('스터디 생성 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    if (errors[name]) setErrors({ ...errors, [name]: '' });
  };

  return (
    <div className="max-w-4xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        <div className="bg-white shadow rounded-lg">
          <div className="px-4 py-5 sm:p-6">
            <h1 className="text-3xl font-bold text-gray-900 mb-8">새 스터디 만들기</h1>
            
            <form onSubmit={handleSubmit} className="space-y-6">
              <div className="grid grid-cols-1 gap-6 sm:grid-cols-2">
                <div className="sm:col-span-2">
                  <label htmlFor="title" className="block text-sm font-medium text-gray-700">
                    스터디 제목 *
                  </label>
                  <input
                    type="text"
                    id="title"
                    name="title"
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.title ? 'border-red-500' : 'border-gray-300'
                    }`}
                    value={formData.title}
                    onChange={handleChange}
                    placeholder="스터디 제목을 입력하세요"
                  />
                  {errors.title && <p className="mt-1 text-sm text-red-600">{errors.title}</p>}
                </div>

                <div className="sm:col-span-2">
                  <label htmlFor="description" className="block text-sm font-medium text-gray-700">
                    스터디 내용 *
                  </label>
                  <textarea
                    id="description"
                    name="description"
                    rows={4}
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.description ? 'border-red-500' : 'border-gray-300'
                    }`}
                    value={formData.description}
                    onChange={handleChange}
                    placeholder="스터디에 대한 자세한 설명을 입력하세요"
                  />
                  {errors.description && <p className="mt-1 text-sm text-red-600">{errors.description}</p>}
                </div>

                <div>
                  <label htmlFor="category" className="block text-sm font-medium text-gray-700">
                    스터디 분류 *
                  </label>
                  <select
                    id="category"
                    name="category"
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.category ? 'border-red-500' : 'border-gray-300'
                    }`}
                    value={formData.category}
                    onChange={handleChange}
                  >
                    <option value="">분류를 선택하세요</option>
                    <option value="프로그래밍">프로그래밍</option>
                    <option value="언어">언어</option>
                    <option value="자격증">자격증</option>
                    <option value="취업준비">취업준비</option>
                    <option value="기타">기타</option>
                  </select>
                  {errors.category && <p className="mt-1 text-sm text-red-600">{errors.category}</p>}
                </div>

                <div>
                  <label htmlFor="mode" className="block text-sm font-medium text-gray-700">
                    스터디 모드 *
                  </label>
                  <select
                    id="mode"
                    name="mode"
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.mode ? 'border-red-500' : 'border-gray-300'
                    }`}
                    value={formData.mode}
                    onChange={handleChange}
                  >
                    <option value="">모드를 선택하세요</option>
                    <option value="온라인">온라인</option>
                    <option value="오프라인">오프라인</option>
                    <option value="하이브리드">하이브리드</option>
                  </select>
                  {errors.mode && <p className="mt-1 text-sm text-red-600">{errors.mode}</p>}
                </div>

                <div>
                  <label htmlFor="location" className="block text-sm font-medium text-gray-700">
                    스터디 위치
                  </label>
                  <input
                    type="text"
                    id="location"
                    name="location"
                    className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                    value={formData.location}
                    onChange={handleChange}
                    placeholder="스터디 장소 (선택사항)"
                  />
                </div>

                <div>
                  <label htmlFor="recruitNumber" className="block text-sm font-medium text-gray-700">
                    모집 인원 *
                  </label>
                  <input
                    type="number"
                    id="recruitNumber"
                    name="recruitNumber"
                    min="1"
                    max="50"
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.recruitNumber ? 'border-red-500' : 'border-gray-300'
                    }`}
                    value={formData.recruitNumber}
                    onChange={handleChange}
                  />
                  {errors.recruitNumber && <p className="mt-1 text-sm text-red-600">{errors.recruitNumber}</p>}
                </div>

                <div>
                  <label htmlFor="studyStartDate" className="block text-sm font-medium text-gray-700">
                    스터디 시작일
                  </label>
                  <input
                    type="date"
                    id="studyStartDate"
                    name="studyStartDate"
                    className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                    value={formData.studyStartDate}
                    onChange={handleChange}
                  />
                </div>

                <div>
                  <label htmlFor="studyEndDate" className="block text-sm font-medium text-gray-700">
                    스터디 종료일
                  </label>
                  <input
                    type="date"
                    id="studyEndDate"
                    name="studyEndDate"
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.studyEndDate ? 'border-red-500' : 'border-gray-300'
                    }`}
                    value={formData.studyEndDate}
                    onChange={handleChange}
                  />
                  {errors.studyEndDate && <p className="mt-1 text-sm text-red-600">{errors.studyEndDate}</p>}
                </div>
              </div>

              <div className="flex justify-end space-x-4 pt-6">
                <button
                  type="button"
                  onClick={() => navigate('/studies')}
                  className="px-4 py-2 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                >
                  취소
                </button>
                <button
                  type="submit"
                  disabled={loading}
                  className="px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50"
                >
                  {loading ? '생성 중...' : '스터디 생성'}
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

const Header = () => {
  const { user, logout } = useAuth();
  
  return (
    <header className="bg-white shadow">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16">
          <div className="flex items-center space-x-8">
            <Link to="/" className="text-xl font-bold text-gray-900">StudyGroup</Link>
            <nav className="flex space-x-4">
              <Link to="/" className="text-gray-700 hover:text-gray-900 px-3 py-2 rounded-md text-sm font-medium">홈</Link>
              <Link to="/studies" className="text-gray-700 hover:text-gray-900 px-3 py-2 rounded-md text-sm font-medium">스터디 목록</Link>
              <Link to="/create-study" className="text-gray-700 hover:text-gray-900 px-3 py-2 rounded-md text-sm font-medium">스터디 만들기</Link>
            </nav>
          </div>
          <div className="flex items-center space-x-4">
                         <Link to="/me" className="text-gray-700 hover:text-indigo-600 font-medium">
               안녕하세요, {user?.username || '사용자'}님!
             </Link>
            <button onClick={logout} className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700">
              로그아웃
            </button>
          </div>
        </div>
      </div>
    </header>
  );
};

const Home = () => {
  const { user } = useAuth();
  
  return (
    <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        <h1 className="text-3xl font-bold text-gray-900 mb-8">StudyGroup에 오신 것을 환영합니다!</h1>
        <p className="text-lg text-gray-600">안녕하세요, {user?.username}님!</p>
      </div>
    </div>
  );
};

const ProtectedRoute = ({ children }) => {
  const { user, loading } = useAuth();

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  if (!user) {
    return <Navigate to="/login" replace />;
  }

  return <>{children}</>;
};

const Layout = ({ children }) => {
  const { user } = useAuth();
  return (
    <div className="min-h-screen bg-gray-50">
      {user && <Header />}
      <main>{children}</main>
    </div>
  );
};

const App = () => {
  return (
    <QueryClientProvider client={queryClient}>
      <Router>
        <AuthProvider>
          <Layout>
            <Routes>
              <Route path="/" element={<ProtectedRoute><Home /></ProtectedRoute>} />
              <Route path="/login" element={<LoginForm />} />
              <Route path="/register" element={<RegisterForm />} />
              <Route path="/me" element={<ProtectedRoute><UserProfile /></ProtectedRoute>} />
              <Route path="/studies" element={<div>스터디 목록 (추후 구현)</div>} />
              <Route path="/create-study" element={<div>스터디 만들기 (추후 구현)</div>} />
              <Route path="*" element={<Navigate to="/" replace />} />
            </Routes>
          </Layout>
          <Toaster position="top-right" />
        </AuthProvider>
      </Router>
    </QueryClientProvider>
  );
};

export default App;
