const CreateStudy = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    category: '',
    mode: '',
    location: '',
    recruitNumber: 1,
    studyStartDate: '',
    studyEndDate: ''
  });
  const [errors, setErrors] = useState({});
  
  // Request form state
  const [showRequestForm, setShowRequestForm] = useState(false);
  const [requestFormData, setRequestFormData] = useState({
    title: '스터디 지원 양식',
    questions: []
  });
  const [requestFormErrors, setRequestFormErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};
    if (!formData.title.trim()) newErrors.title = '스터디 제목을 입력해주세요.';
    if (!formData.description.trim()) newErrors.description = '스터디 내용을 입력해주세요.';
    if (!formData.category) newErrors.category = '스터디 분류를 선택해주세요.';
    if (!formData.mode) newErrors.mode = '스터디 모드를 선택해주세요.';
    if (formData.recruitNumber < 1) newErrors.recruitNumber = '모집 인원은 1명 이상이어야 합니다.';
    if (formData.studyStartDate && formData.studyEndDate && 
        new Date(formData.studyStartDate) > new Date(formData.studyEndDate)) {
      newErrors.studyEndDate = '종료일은 시작일보다 늦어야 합니다.';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateRequestForm = () => {
    const newErrors = {};
    if (!requestFormData.title.trim()) newErrors.title = '양식 제목을 입력해주세요.';
    if (requestFormData.questions.length === 0) newErrors.questions = '최소 하나의 질문을 추가해주세요.';
    
    // Validate each question
    requestFormData.questions.forEach((question, index) => {
      if (!question.questionText.trim()) {
        newErrors[`question_${index}`] = '질문 내용을 입력해주세요.';
      }
    });
    
    setRequestFormErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;
    
    // Validate request form if it's enabled
    if (showRequestForm && !validateRequestForm()) return;

    setLoading(true);
    try {
      // First, create the study
      const studyResponse = await fetch('http://localhost:3001/api/post', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          ...formData,
          recruitNumber: parseInt(formData.recruitNumber),
          studyStartDate: formData.studyStartDate || null,
          studyEndDate: formData.studyEndDate || null
        }),
      });

      const studyData = await studyResponse.json();
      
      if (studyResponse.ok) {
        // If request form is enabled, create it
        if (showRequestForm && requestFormData.questions.length > 0) {
          try {
            const formResponse = await fetch('http://localhost:3001/api/requestform', {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem('token')}`
              },
              body: JSON.stringify({
                postId: studyData.data.id,
                title: requestFormData.title,
                questions: requestFormData.questions.map((q, index) => ({
                  questionText: q.questionText,
                  type: q.type,
                  isRequired: q.isRequired,
                  order: index + 1
                }))
              }),
            });

            if (formResponse.ok) {
              toast.success('스터디와 지원 양식이 성공적으로 생성되었습니다!');
            } else {
              toast.success('스터디가 생성되었지만 지원 양식 생성에 실패했습니다.');
            }
          } catch (formError) {
            console.error('Create request form error:', formError);
            toast.success('스터디가 생성되었지만 지원 양식 생성에 실패했습니다.');
          }
        } else {
          toast.success('스터디가 성공적으로 생성되었습니다!');
        }
        navigate('/studies');
      } else {
        toast.error(studyData.message || '스터디 생성에 실패했습니다.');
      }
    } catch (error) {
      console.error('Create study error:', error);
      toast.error('스터디 생성 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    if (errors[name]) setErrors({ ...errors, [name]: '' });
  };

  // Request form helper functions
  const addQuestion = () => {
    setRequestFormData({
      ...requestFormData,
      questions: [
        ...requestFormData.questions,
        {
          questionText: '',
          type: 'short_answer',
          isRequired: true,
          options: []
        }
      ]
    });
  };

  const removeQuestion = (index) => {
    const newQuestions = requestFormData.questions.filter((_, i) => i !== index);
    setRequestFormData({
      ...requestFormData,
      questions: newQuestions
    });
  };

  const updateQuestion = (index, field, value) => {
    const newQuestions = [...requestFormData.questions];
    newQuestions[index] = { ...newQuestions[index], [field]: value };
    setRequestFormData({
      ...requestFormData,
      questions: newQuestions
    });
  };

  const handleRequestFormChange = (e) => {
    const { name, value } = e.target;
    setRequestFormData({ ...requestFormData, [name]: value });
    if (requestFormErrors[name]) setRequestFormErrors({ ...requestFormErrors, [name]: '' });
  };

  return (
    <div className="max-w-4xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        <div className="bg-white shadow rounded-lg">
          <div className="px-4 py-5 sm:p-6">
            <h1 className="text-3xl font-bold text-gray-900 mb-8">새 스터디 만들기</h1>
            
            <form onSubmit={handleSubmit} className="space-y-6">
              <div className="grid grid-cols-1 gap-6 sm:grid-cols-2">
                <div className="sm:col-span-2">
                  <label htmlFor="title" className="block text-sm font-medium text-gray-700">
                    스터디 제목 *
                  </label>
                  <input
                    type="text"
                    id="title"
                    name="title"
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.title ? 'border-red-500' : 'border-gray-300'
                    }`}
                    value={formData.title}
                    onChange={handleChange}
                    placeholder="스터디 제목을 입력하세요"
                  />
                  {errors.title && <p className="mt-1 text-sm text-red-600">{errors.title}</p>}
                </div>

                <div className="sm:col-span-2">
                  <label htmlFor="description" className="block text-sm font-medium text-gray-700">
                    스터디 내용 *
                  </label>
                  <textarea
                    id="description"
                    name="description"
                    rows={4}
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.description ? 'border-red-500' : 'border-gray-300'
                    }`}
                    value={formData.description}
                    onChange={handleChange}
                    placeholder="스터디에 대한 자세한 설명을 입력하세요"
                  />
                  {errors.description && <p className="mt-1 text-sm text-red-600">{errors.description}</p>}
                </div>

                <div>
                  <label htmlFor="category" className="block text-sm font-medium text-gray-700">
                    스터디 분류 *
                  </label>
                  <select
                    id="category"
                    name="category"
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.category ? 'border-red-500' : 'border-gray-300'
                    }`}
                    value={formData.category}
                    onChange={handleChange}
                  >
                    <option value="">분류를 선택하세요</option>
                    <option value="프로그래밍">프로그래밍</option>
                    <option value="언어">언어</option>
                    <option value="자격증">자격증</option>
                    <option value="취업준비">취업준비</option>
                    <option value="기타">기타</option>
                  </select>
                  {errors.category && <p className="mt-1 text-sm text-red-600">{errors.category}</p>}
                </div>

                <div>
                  <label htmlFor="mode" className="block text-sm font-medium text-gray-700">
                    스터디 모드 *
                  </label>
                  <select
                    id="mode"
                    name="mode"
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.mode ? 'border-red-500' : 'border-gray-300'
                    }`}
                    value={formData.mode}
                    onChange={handleChange}
                  >
                    <option value="">모드를 선택하세요</option>
                    <option value="온라인">온라인</option>
                    <option value="오프라인">오프라인</option>
                    <option value="하이브리드">하이브리드</option>
                  </select>
                  {errors.mode && <p className="mt-1 text-sm text-red-600">{errors.mode}</p>}
                </div>

                <div>
                  <label htmlFor="location" className="block text-sm font-medium text-gray-700">
                    스터디 위치
                  </label>
                  <input
                    type="text"
                    id="location"
                    name="location"
                    className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                    value={formData.location}
                    onChange={handleChange}
                    placeholder="스터디 장소 (선택사항)"
                  />
                </div>

                <div>
                  <label htmlFor="recruitNumber" className="block text-sm font-medium text-gray-700">
                    모집 인원 *
                  </label>
                  <input
                    type="number"
                    id="recruitNumber"
                    name="recruitNumber"
                    min="1"
                    max="50"
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.recruitNumber ? 'border-red-500' : 'border-gray-300'
                    }`}
                    value={formData.recruitNumber}
                    onChange={handleChange}
                  />
                  {errors.recruitNumber && <p className="mt-1 text-sm text-red-600">{errors.recruitNumber}</p>}
                </div>

                <div>
                  <label htmlFor="studyStartDate" className="block text-sm font-medium text-gray-700">
                    스터디 시작일
                  </label>
                  <input
                    type="date"
                    id="studyStartDate"
                    name="studyStartDate"
                    className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                    value={formData.studyStartDate}
                    onChange={handleChange}
                  />
                </div>

                <div>
                  <label htmlFor="studyEndDate" className="block text-sm font-medium text-gray-700">
                    스터디 종료일
                  </label>
                  <input
                    type="date"
                    id="studyEndDate"
                    name="studyEndDate"
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.studyEndDate ? 'border-red-500' : 'border-gray-300'
                    }`}
                    value={formData.studyEndDate}
                    onChange={handleChange}
                  />
                  {errors.studyEndDate && <p className="mt-1 text-sm text-red-600">{errors.studyEndDate}</p>}
                </div>
              </div>

              {/* Request Form Section */}
              <div className="border-t pt-6">
                <div className="flex items-center justify-between mb-4">
                  <h3 className="text-lg font-medium text-gray-900">지원 양식 설정</h3>
                  <button
                    type="button"
                    onClick={() => setShowRequestForm(!showRequestForm)}
                    className="text-sm text-indigo-600 hover:text-indigo-500"
                  >
                    {showRequestForm ? '양식 숨기기' : '양식 추가하기'}
                  </button>
                </div>
                
                {showRequestForm && (
                  <div className="bg-gray-50 p-4 rounded-lg">
                    <div className="mb-4">
                      <label htmlFor="formTitle" className="block text-sm font-medium text-gray-700 mb-2">
                        양식 제목
                      </label>
                      <input
                        type="text"
                        id="formTitle"
                        name="title"
                        className={`block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                          requestFormErrors.title ? 'border-red-500' : 'border-gray-300'
                        }`}
                        value={requestFormData.title}
                        onChange={handleRequestFormChange}
                        placeholder="지원 양식 제목을 입력하세요"
                      />
                      {requestFormErrors.title && <p className="mt-1 text-sm text-red-600">{requestFormErrors.title}</p>}
                    </div>

                    <div className="mb-4">
                      <div className="flex items-center justify-between mb-2">
                        <label className="block text-sm font-medium text-gray-700">
                          질문 목록
                        </label>
                        <button
                          type="button"
                          onClick={addQuestion}
                          className="text-sm bg-indigo-600 text-white px-3 py-1 rounded hover:bg-indigo-700"
                        >
                          질문 추가
                        </button>
                      </div>
                      
                      {requestFormErrors.questions && (
                        <p className="text-sm text-red-600 mb-2">{requestFormErrors.questions}</p>
                      )}

                      {requestFormData.questions.map((question, index) => (
                        <div key={index} className="bg-white p-4 rounded border mb-3">
                          <div className="flex items-center justify-between mb-2">
                            <span className="text-sm font-medium text-gray-700">질문 {index + 1}</span>
                            <button
                              type="button"
                              onClick={() => removeQuestion(index)}
                              className="text-red-600 hover:text-red-800 text-sm"
                            >
                              삭제
                            </button>
                          </div>
                          
                          <div className="grid grid-cols-1 gap-3">
                            <div>
                              <label className="block text-sm text-gray-600 mb-1">질문 내용</label>
                              <input
                                type="text"
                                className={`block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                                  requestFormErrors[`question_${index}`] ? 'border-red-500' : 'border-gray-300'
                                }`}
                                value={question.questionText}
                                onChange={(e) => updateQuestion(index, 'questionText', e.target.value)}
                                placeholder="질문을 입력하세요"
                              />
                              {requestFormErrors[`question_${index}`] && (
                                <p className="mt-1 text-sm text-red-600">{requestFormErrors[`question_${index}`]}</p>
                              )}
                            </div>
                            
                            <div className="grid grid-cols-2 gap-3">
                              <div>
                                <label className="block text-sm text-gray-600 mb-1">답변 유형</label>
                                                         <select
                           className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                           value={question.type}
                           onChange={(e) => updateQuestion(index, 'type', e.target.value)}
                         >
                           <option value="short_answer">단답형</option>
                           <option value="long_answer">장문형</option>
                           <option value="checkbox">체크박스</option>
                           <option value="multiple_choice">객관식 선택형</option>
                         </select>
                              </div>
                              
                              <div className="flex items-center">
                                <input
                                  type="checkbox"
                                  id={`required_${index}`}
                                  className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300 rounded"
                                  checked={question.isRequired}
                                  onChange={(e) => updateQuestion(index, 'isRequired', e.target.checked)}
                                />
                                <label htmlFor={`required_${index}`} className="ml-2 block text-sm text-gray-700">
                                  필수 답변
                                </label>
                              </div>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>

              <div className="flex justify-end space-x-4 pt-6">
                <button
                  type="button"
                  onClick={() => navigate('/studies')}
                  className="px-4 py-2 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                >
                  취소
                </button>
                <button
                  type="submit"
                  disabled={loading}
                  className="px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50"
                >
                  {loading ? '생성 중...' : '스터디 생성'}
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

const Header = () => {
  const { user, logout } = useAuth();
  
  return (
    <header className="bg-white shadow">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16">
          <div className="flex items-center space-x-8">
            <Link to="/" className="text-xl font-bold text-gray-900">StudyGroup</Link>
            <nav className="flex space-x-4">
              <Link to="/" className="text-gray-700 hover:text-gray-900 px-3 py-2 rounded-md text-sm font-medium">홈</Link>
              <Link to="/studies" className="text-gray-700 hover:text-gray-900 px-3 py-2 rounded-md text-sm font-medium">스터디 목록</Link>
              <Link to="/create-study" className="text-gray-700 hover:text-gray-900 px-3 py-2 rounded-md text-sm font-medium">스터디 만들기</Link>
            </nav>
          </div>
          <div className="flex items-center space-x-4">
                         <Link to="/me" className="text-gray-700 hover:text-indigo-600 font-medium">
               안녕하세요, {user?.username || '사용자'}님!
             </Link>
            <button onClick={logout} className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700">
              로그아웃
            </button>
          </div>
        </div>
      </div>
    </header>
  );
};

const Home = () => {
  const { user } = useAuth();
  
  return (
    <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        <h1 className="text-3xl font-bold text-gray-900 mb-8">StudyGroup에 오신 것을 환영합니다!</h1>
        <p className="text-lg text-gray-600">안녕하세요, {user?.username}님!</p>
      </div>
    </div>
  );
};

const StudyList = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [studies, setStudies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedMode, setSelectedMode] = useState('');
  const [selectedStatus, setSelectedStatus] = useState('');
  const [selectedLocation, setSelectedLocation] = useState('');
  const [minMembers, setMinMembers] = useState('');
  const [maxMembers, setMaxMembers] = useState('');
  const [showAdvancedFilters, setShowAdvancedFilters] = useState(false);
  const [searchHistory, setSearchHistory] = useState([]);
  const [showSearchHistory, setShowSearchHistory] = useState(false);
  const [debouncedSearchTerm, setDebouncedSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('createdAt');
  const [sortOrder, setSortOrder] = useState('desc');
  const [savedSearches, setSavedSearches] = useState([]);
  const [showSavedSearches, setShowSavedSearches] = useState(false);

  useEffect(() => {
    fetchStudies();
    // Load search history from localStorage
    const savedHistory = localStorage.getItem('studySearchHistory');
    if (savedHistory) {
      setSearchHistory(JSON.parse(savedHistory));
    }
    
    // Load search preferences from localStorage
    const savedPreferences = localStorage.getItem('studySearchPreferences');
    if (savedPreferences) {
      const preferences = JSON.parse(savedPreferences);
      setSelectedCategory(preferences.category || '');
      setSelectedMode(preferences.mode || '');
      setSelectedStatus(preferences.status || '');
      setSortBy(preferences.sortBy || 'createdAt');
      setSortOrder(preferences.sortOrder || 'desc');
    }
    
    // Load saved searches from localStorage
    const savedSearchesData = localStorage.getItem('savedSearches');
    if (savedSearchesData) {
      setSavedSearches(JSON.parse(savedSearchesData));
    }
  }, []);

  // Debounce search term
  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedSearchTerm(searchTerm);
    }, 300);

    return () => clearTimeout(timer);
  }, [searchTerm]);

  const saveSearchHistory = (term) => {
    if (!term.trim()) return;
    
    const newHistory = [term, ...searchHistory.filter(item => item !== term)].slice(0, 10);
    setSearchHistory(newHistory);
    localStorage.setItem('studySearchHistory', JSON.stringify(newHistory));
  };

  const clearSearchHistory = () => {
    setSearchHistory([]);
    localStorage.removeItem('studySearchHistory');
  };

  const saveSearchPreferences = () => {
    const preferences = {
      category: selectedCategory,
      mode: selectedMode,
      status: selectedStatus,
      sortBy,
      sortOrder
    };
    localStorage.setItem('studySearchPreferences', JSON.stringify(preferences));
  };

  // Save preferences when they change
  useEffect(() => {
    saveSearchPreferences();
  }, [selectedCategory, selectedMode, selectedStatus, sortBy, sortOrder]);

  const saveCurrentSearch = () => {
    const searchName = prompt('검색 조건의 이름을 입력하세요:');
    if (!searchName) return;
    
    const currentSearch = {
      id: Date.now(),
      name: searchName,
      searchTerm,
      category: selectedCategory,
      mode: selectedMode,
      status: selectedStatus,
      location: selectedLocation,
      minMembers,
      maxMembers,
      sortBy,
      sortOrder,
      createdAt: new Date().toISOString()
    };
    
    const newSavedSearches = [currentSearch, ...savedSearches.filter(s => s.name !== searchName)].slice(0, 5);
    setSavedSearches(newSavedSearches);
    localStorage.setItem('savedSearches', JSON.stringify(newSavedSearches));
  };

  const loadSavedSearch = (savedSearch) => {
    setSearchTerm(savedSearch.searchTerm || '');
    setSelectedCategory(savedSearch.category || '');
    setSelectedMode(savedSearch.mode || '');
    setSelectedStatus(savedSearch.status || '');
    setSelectedLocation(savedSearch.location || '');
    setMinMembers(savedSearch.minMembers || '');
    setMaxMembers(savedSearch.maxMembers || '');
    setSortBy(savedSearch.sortBy || 'createdAt');
    setSortOrder(savedSearch.sortOrder || 'desc');
    setShowSavedSearches(false);
  };

  const deleteSavedSearch = (searchId) => {
    const newSavedSearches = savedSearches.filter(s => s.id !== searchId);
    setSavedSearches(newSavedSearches);
    localStorage.setItem('savedSearches', JSON.stringify(newSavedSearches));
  };

  const fetchStudies = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:3001/api/post', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setStudies(data.data || []);
      } else {
        setError('스터디 목록을 불러오는데 실패했습니다.');
      }
    } catch (error) {
      console.error('Fetch studies error:', error);
      setError('스터디 목록을 불러오는 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  const filteredStudies = studies.filter(study => {
    const matchesSearch = !debouncedSearchTerm || 
                         study.title.toLowerCase().includes(debouncedSearchTerm.toLowerCase()) ||
                         study.description.toLowerCase().includes(debouncedSearchTerm.toLowerCase()) ||
                         (study.author?.username && study.author.username.toLowerCase().includes(debouncedSearchTerm.toLowerCase()));
    
    const matchesCategory = !selectedCategory || study.category === selectedCategory;
    const matchesMode = !selectedMode || study.mode === selectedMode;
    const matchesLocation = !selectedLocation || 
                           (study.location && study.location.toLowerCase().includes(selectedLocation.toLowerCase()));
    
    // Status filtering
    let matchesStatus = true;
    if (selectedStatus === 'recruiting') {
      matchesStatus = !study.isClosed && study.currentNumber < study.recruitNumber;
    } else if (selectedStatus === 'full') {
      matchesStatus = study.currentNumber >= study.recruitNumber;
    } else if (selectedStatus === 'closed') {
      matchesStatus = study.isClosed;
    }
    
    // Member count filtering
    const matchesMinMembers = !minMembers || study.recruitNumber >= parseInt(minMembers);
    const matchesMaxMembers = !maxMembers || study.recruitNumber <= parseInt(maxMembers);
    
    return matchesSearch && matchesCategory && matchesMode && matchesStatus && 
           matchesLocation && matchesMinMembers && matchesMaxMembers;
  }).sort((a, b) => {
    let aValue, bValue;
    
    switch (sortBy) {
      case 'title':
        aValue = a.title.toLowerCase();
        bValue = b.title.toLowerCase();
        break;
      case 'category':
        aValue = a.category.toLowerCase();
        bValue = b.category.toLowerCase();
        break;
      case 'recruitNumber':
        aValue = a.recruitNumber;
        bValue = b.recruitNumber;
        break;
      case 'currentNumber':
        aValue = a.currentNumber || 0;
        bValue = b.currentNumber || 0;
        break;
      case 'createdAt':
      default:
        aValue = new Date(a.createdAt);
        bValue = new Date(b.createdAt);
        break;
    }
    
    if (sortOrder === 'asc') {
      return aValue > bValue ? 1 : -1;
    } else {
      return aValue < bValue ? 1 : -1;
    }
  });

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    saveSearchHistory(searchTerm);
    setShowSearchHistory(false);
  };

  const handleSearchHistoryClick = (term) => {
    setSearchTerm(term);
    setShowSearchHistory(false);
  };

  // Highlight search terms in text
  const highlightText = (text, searchTerm) => {
    if (!searchTerm || !text) return text;
    
    const regex = new RegExp(`(${searchTerm})`, 'gi');
    const parts = text.split(regex);
    
    return parts.map((part, index) => 
      regex.test(part) ? (
        <mark key={index} className="bg-yellow-200 px-1 rounded">
          {part}
        </mark>
      ) : part
    );
  };

  // Close search history when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (showSearchHistory && !event.target.closest('.search-container')) {
        setShowSearchHistory(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showSearchHistory]);

  const handleJoinStudy = async (studyId) => {
    try {
      const response = await fetch(`http://localhost:3001/api/post/${studyId}/join`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        toast.success('스터디에 참여 신청되었습니다!');
        fetchStudies(); // 목록 새로고침
      } else {
        const data = await response.json();
        toast.error(data.message || '스터디 참여 신청에 실패했습니다.');
      }
    } catch (error) {
      console.error('Join study error:', error);
      toast.error('스터디 참여 신청 중 오류가 발생했습니다.');
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '미정';
    return new Date(dateString).toLocaleDateString('ko-KR');
  };

  const getStatusBadge = (study) => {
    if (study.isClosed) {
      return <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">모집 완료</span>;
    }
    if (study.currentNumber >= study.recruitNumber) {
      return <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">인원 마감</span>;
    }
    return <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">모집 중</span>;
  };

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="flex justify-center">
            <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-indigo-600"></div>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="text-center">
            <h2 className="text-2xl font-bold text-gray-900 mb-4">오류가 발생했습니다</h2>
            <p className="text-gray-600 mb-4">{error}</p>
            <button
              onClick={fetchStudies}
              className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700"
            >
              다시 시도
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900">스터디 목록</h1>
          <button
            onClick={() => navigate('/create-study')}
            className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700"
          >
            새 스터디 만들기
          </button>
        </div>

        {/* 검색 및 필터 */}
        <div className="bg-white p-6 rounded-lg shadow mb-6">
          {/* 기본 검색 및 필터 */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-4">
            <div className="relative search-container">
              <label className="block text-sm font-medium text-gray-700 mb-2">검색</label>
              <form onSubmit={handleSearchSubmit} className="relative">
                <input
                  type="text"
                  placeholder="스터디 제목, 내용, 작성자로 검색"
                  value={searchTerm}
                  onChange={(e) => {
                    setSearchTerm(e.target.value);
                    setShowSearchHistory(true);
                  }}
                  onFocus={() => setShowSearchHistory(true)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                />
                <button
                  type="submit"
                  className="absolute right-2 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
                >
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                  </svg>
                </button>
              </form>
              
              {/* 검색 기록 드롭다운 */}
              {showSearchHistory && searchHistory.length > 0 && (
                <div className="absolute z-10 w-full mt-1 bg-white border border-gray-300 rounded-md shadow-lg">
                  <div className="p-2 border-b border-gray-200 flex justify-between items-center">
                    <span className="text-sm font-medium text-gray-700">최근 검색어</span>
                    <button
                      onClick={clearSearchHistory}
                      className="text-xs text-red-600 hover:text-red-800"
                    >
                      전체 삭제
                    </button>
                  </div>
                  {searchHistory.map((term, index) => (
                    <button
                      key={index}
                      onClick={() => handleSearchHistoryClick(term)}
                      className="w-full text-left px-3 py-2 text-sm text-gray-700 hover:bg-gray-100 flex items-center"
                    >
                      <svg className="w-4 h-4 mr-2 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                      </svg>
                      {term}
                    </button>
                  ))}
                </div>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">분류</label>
              <select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
              >
                <option value="">전체</option>
                <option value="프로그래밍">프로그래밍</option>
                <option value="언어">언어</option>
                <option value="자격증">자격증</option>
                <option value="취업준비">취업준비</option>
                <option value="기타">기타</option>
              </select>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">모드</label>
              <select
                value={selectedMode}
                onChange={(e) => setSelectedMode(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
              >
                <option value="">전체</option>
                <option value="온라인">온라인</option>
                <option value="오프라인">오프라인</option>
                <option value="하이브리드">하이브리드</option>
              </select>
            </div>
            
            <div className="flex items-end space-x-2">
              <button
                onClick={() => setShowAdvancedFilters(!showAdvancedFilters)}
                className="flex-1 bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 text-sm"
              >
                {showAdvancedFilters ? '고급 필터 숨기기' : '고급 필터'}
              </button>
              <button
                onClick={() => setShowSavedSearches(!showSavedSearches)}
                className="flex-1 bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700 text-sm"
              >
                저장된 검색
              </button>
              <button
                onClick={() => {
                  setSearchTerm('');
                  setSelectedCategory('');
                  setSelectedMode('');
                  setSelectedStatus('');
                  setSelectedLocation('');
                  setMinMembers('');
                  setMaxMembers('');
                }}
                className="flex-1 bg-gray-600 text-white px-4 py-2 rounded-md hover:bg-gray-700 text-sm"
              >
                초기화
              </button>
            </div>
          </div>

          {/* 저장된 검색 드롭다운 */}
          {showSavedSearches && (
            <div className="border-t border-gray-200 pt-4">
              <div className="flex items-center justify-between mb-3">
                <h4 className="text-sm font-medium text-gray-700">저장된 검색 조건</h4>
                <button
                  onClick={saveCurrentSearch}
                  className="text-sm text-indigo-600 hover:text-indigo-800 underline"
                >
                  현재 검색 저장
                </button>
              </div>
              {savedSearches.length === 0 ? (
                <p className="text-sm text-gray-500 text-center py-4">저장된 검색 조건이 없습니다.</p>
              ) : (
                <div className="space-y-2">
                  {savedSearches.map((savedSearch) => (
                    <div key={savedSearch.id} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                      <div className="flex-1">
                        <div className="flex items-center space-x-2">
                          <button
                            onClick={() => loadSavedSearch(savedSearch)}
                            className="text-sm font-medium text-gray-900 hover:text-indigo-600"
                          >
                            {savedSearch.name}
                          </button>
                          <span className="text-xs text-gray-500">
                            {new Date(savedSearch.createdAt).toLocaleDateString()}
                          </span>
                        </div>
                        <div className="text-xs text-gray-600 mt-1">
                          {savedSearch.searchTerm && `검색어: ${savedSearch.searchTerm}`}
                          {savedSearch.category && ` | 분류: ${savedSearch.category}`}
                          {savedSearch.mode && ` | 모드: ${savedSearch.mode}`}
                        </div>
                      </div>
                      <button
                        onClick={() => deleteSavedSearch(savedSearch.id)}
                        className="text-red-600 hover:text-red-800 text-sm"
                        title="삭제"
                      >
                        <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                        </svg>
                      </button>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}

          {/* 고급 필터 */}
          {showAdvancedFilters && (
            <div className="border-t border-gray-200 pt-4">
              <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">상태</label>
                  <select
                    value={selectedStatus}
                    onChange={(e) => setSelectedStatus(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                  >
                    <option value="">전체</option>
                    <option value="recruiting">모집 중</option>
                    <option value="full">인원 마감</option>
                    <option value="closed">모집 완료</option>
                  </select>
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">위치</label>
                  <input
                    type="text"
                    placeholder="위치로 검색"
                    value={selectedLocation}
                    onChange={(e) => setSelectedLocation(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                  />
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">최소 인원</label>
                  <input
                    type="number"
                    placeholder="최소"
                    value={minMembers}
                    onChange={(e) => setMinMembers(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                  />
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">최대 인원</label>
                  <input
                    type="number"
                    placeholder="최대"
                    value={maxMembers}
                    onChange={(e) => setMaxMembers(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                  />
                </div>
                
                <div className="flex items-end">
                  <div className="w-full text-center">
                    <p className="text-sm text-gray-600 mb-1">검색 결과</p>
                    <p className="text-lg font-semibold text-indigo-600">{filteredStudies.length}개</p>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* 검색 결과 요약 및 정렬 */}
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between mb-6">
          {/* 검색 결과 요약 */}
          {debouncedSearchTerm || selectedCategory || selectedMode || selectedStatus || selectedLocation || minMembers || maxMembers ? (
            <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-4 sm:mb-0 flex-1">
              <div className="flex items-center justify-between">
                <div>
                  <h3 className="text-sm font-medium text-blue-900">검색 결과</h3>
                  <p className="text-sm text-blue-700 mt-1">
                    총 <span className="font-semibold">{filteredStudies.length}개</span>의 스터디를 찾았습니다
                    {debouncedSearchTerm && (
                      <span className="ml-2">
                        (검색어: <span className="font-semibold">"{debouncedSearchTerm}"</span>)
                      </span>
                    )}
                  </p>
                </div>
                <button
                  onClick={() => {
                    setSearchTerm('');
                    setSelectedCategory('');
                    setSelectedMode('');
                    setSelectedStatus('');
                    setSelectedLocation('');
                    setMinMembers('');
                    setMaxMembers('');
                  }}
                  className="text-sm text-blue-600 hover:text-blue-800 underline"
                >
                  검색 조건 초기화
                </button>
              </div>
            </div>
          ) : (
            <div className="mb-4 sm:mb-0">
              <p className="text-sm text-gray-600">
                총 <span className="font-semibold">{filteredStudies.length}개</span>의 스터디
              </p>
            </div>
          )}

          {/* 정렬 옵션 */}
          {filteredStudies.length > 0 && (
            <div className="flex items-center space-x-2">
              <label className="text-sm font-medium text-gray-700">정렬:</label>
              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                className="px-3 py-1 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
              >
                <option value="createdAt">최신순</option>
                <option value="title">제목순</option>
                <option value="category">분류순</option>
                <option value="recruitNumber">모집인원순</option>
                <option value="currentNumber">현재인원순</option>
              </select>
              <button
                onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}
                className="p-1 text-gray-400 hover:text-gray-600"
                title={sortOrder === 'asc' ? '내림차순' : '오름차순'}
              >
                {sortOrder === 'asc' ? (
                  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 15l7-7 7 7" />
                  </svg>
                ) : (
                  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                  </svg>
                )}
              </button>
            </div>
          )}
        </div>

        {/* 스터디 목록 */}
        {filteredStudies.length === 0 ? (
          <div className="text-center py-12">
            <div className="mb-4">
              <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
              </svg>
            </div>
            <h3 className="text-lg font-medium text-gray-900 mb-2">
              {studies.length === 0 ? '스터디가 없습니다' : '검색 결과가 없습니다'}
            </h3>
            <p className="text-gray-600 mb-4">
              {studies.length === 0 
                ? '첫 번째 스터디를 만들어보세요!' 
                : '다른 검색 조건을 시도해보세요.'}
            </p>
            {studies.length === 0 ? (
              <button
                onClick={() => navigate('/create-study')}
                className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700"
              >
                스터디 만들기
              </button>
            ) : (
              <button
                onClick={() => {
                  setSearchTerm('');
                  setSelectedCategory('');
                  setSelectedMode('');
                  setSelectedStatus('');
                  setSelectedLocation('');
                  setMinMembers('');
                  setMaxMembers('');
                }}
                className="bg-gray-600 text-white px-4 py-2 rounded-md hover:bg-gray-700"
              >
                검색 조건 초기화
              </button>
            )}
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredStudies.map((study) => (
              <div key={study.id} className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow">
                <div className="p-6">
                  <div className="flex justify-between items-start mb-4">
                    <h3 className="text-xl font-semibold text-gray-900 line-clamp-2">
                      {highlightText(study.title, debouncedSearchTerm)}
                    </h3>
                    {getStatusBadge(study)}
                  </div>
                  
                  <p className="text-gray-600 text-sm mb-4 line-clamp-3">
                    {highlightText(study.description, debouncedSearchTerm)}
                  </p>
                  
                  <div className="space-y-2 mb-4">
                    <div className="flex items-center text-sm text-gray-500">
                      <span className="font-medium mr-2">분류:</span>
                      <span className="bg-blue-100 text-blue-800 px-2 py-1 rounded text-xs">
                        {study.category}
                      </span>
                    </div>
                    <div className="flex items-center text-sm text-gray-500">
                      <span className="font-medium mr-2">모드:</span>
                      <span className="bg-green-100 text-green-800 px-2 py-1 rounded text-xs">
                        {study.mode}
                      </span>
                    </div>
                    {study.location && (
                      <div className="flex items-center text-sm text-gray-500">
                        <span className="font-medium mr-2">위치:</span>
                        <span>{study.location}</span>
                      </div>
                    )}
                    <div className="flex items-center text-sm text-gray-500">
                      <span className="font-medium mr-2">인원:</span>
                      <span>{study.currentNumber || 1}/{study.recruitNumber}명</span>
                    </div>
                    <div className="flex items-center text-sm text-gray-500">
                      <span className="font-medium mr-2">작성자:</span>
                      <span>{highlightText(study.author?.username || '알 수 없음', debouncedSearchTerm)}</span>
                    </div>
                    <div className="flex items-center text-sm text-gray-500">
                      <span className="font-medium mr-2">작성일:</span>
                      <span>{formatDate(study.createdAt)}</span>
                    </div>
                  </div>
                  
                  <div className="flex space-x-2">
                    <button
                      onClick={() => navigate(`/studies/${study.id}`)}
                      className="flex-1 bg-indigo-600 text-white px-3 py-2 rounded-md hover:bg-indigo-700 text-sm"
                    >
                      상세보기
                    </button>
                    {!study.isClosed && study.currentNumber < study.recruitNumber && (
                      <button
                        onClick={() => handleJoinStudy(study.id)}
                        className="flex-1 bg-green-600 text-white px-3 py-2 rounded-md hover:bg-green-700 text-sm"
                      >
                        참여신청
                      </button>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

const StudyDetail = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const { id } = useParams();
  const [study, setStudy] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [sessions, setSessions] = useState([]);
  const [members, setMembers] = useState([]);
  const [activeTab, setActiveTab] = useState('info');

  useEffect(() => {
    if (id) {
      fetchStudyDetail();
      fetchStudySessions();
      fetchStudyMembers();
    }
  }, [id]);

  const fetchStudyDetail = async () => {
    try {
      setLoading(true);
      console.log('Fetching study detail for ID:', id);
      const token = localStorage.getItem('token');
      console.log('Token for study detail:', token);
      
      const response = await fetch(`http://localhost:3001/api/post/${id}/detail`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      console.log('Study detail response status:', response.status);
      
      if (response.ok) {
        const data = await response.json();
        console.log('Study detail data:', data);
        setStudy(data.data || data);
      } else {
        const errorData = await response.json();
        console.error('Study detail error response:', errorData);
        setError('스터디 정보를 불러오는데 실패했습니다.');
      }
    } catch (error) {
      console.error('Fetch study detail error:', error);
      setError('스터디 정보를 불러오는 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  const fetchStudySessions = async () => {
    try {
      const response = await fetch(`http://localhost:3001/api/post/${id}/sessions`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setSessions(data.data || data || []);
      }
    } catch (error) {
      console.error('Fetch sessions error:', error);
    }
  };

  const fetchStudyMembers = async () => {
    try {
      const response = await fetch(`http://localhost:3001/api/post/${id}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setMembers(data.data?.studymemberships || data.studymemberships || []);
      }
    } catch (error) {
      console.error('Fetch members error:', error);
    }
  };

  const handleJoinStudy = async () => {
    try {
      const response = await fetch(`http://localhost:3001/api/post/${id}/join`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        toast.success('스터디에 참여 신청되었습니다!');
        fetchStudyDetail();
        fetchStudyMembers();
      } else {
        const data = await response.json();
        toast.error(data.message || '스터디 참여 신청에 실패했습니다.');
      }
    } catch (error) {
      console.error('Join study error:', error);
      toast.error('스터디 참여 신청 중 오류가 발생했습니다.');
    }
  };

  const handleLeaveStudy = async () => {
    try {
      const response = await fetch(`http://localhost:3001/api/post/${id}/leave`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        toast.success('스터디에서 탈퇴했습니다.');
        fetchStudyDetail();
        fetchStudyMembers();
      } else {
        const data = await response.json();
        toast.error(data.message || '스터디 탈퇴에 실패했습니다.');
      }
    } catch (error) {
      console.error('Leave study error:', error);
      toast.error('스터디 탈퇴 중 오류가 발생했습니다.');
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '미정';
    return new Date(dateString).toLocaleDateString('ko-KR');
  };

  const formatDateTime = (dateString) => {
    if (!dateString) return '미정';
    return new Date(dateString).toLocaleString('ko-KR');
  };

  const getStatusBadge = (study) => {
    if (study?.isClosed) {
      return <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">모집 완료</span>;
    }
    if (study?.currentNumber >= study?.recruitNumber) {
      return <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">인원 마감</span>;
    }
    return <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">모집 중</span>;
  };

  const isAuthor = study?.author?.id === user?.id;
  const isMember = members.some(member => member.user?.id === user?.id);

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="flex justify-center">
            <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-indigo-600"></div>
          </div>
        </div>
      </div>
    );
  }

  if (error || !study) {
    return (
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="text-center">
            <h2 className="text-2xl font-bold text-gray-900 mb-4">오류가 발생했습니다</h2>
            <p className="text-gray-600 mb-4">{error || '스터디를 찾을 수 없습니다.'}</p>
            <button
              onClick={() => navigate('/studies')}
              className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700"
            >
              스터디 목록으로 돌아가기
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        {/* 헤더 */}
        <div className="mb-8">
          <div className="flex items-center justify-between mb-4">
            <button
              onClick={() => navigate('/studies')}
              className="text-indigo-600 hover:text-indigo-800 font-medium"
            >
              ← 스터디 목록으로 돌아가기
            </button>
            <div className="flex space-x-2">
              {isAuthor && (
                <button
                  onClick={() => navigate(`/studies/${id}/edit`)}
                  className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700"
                >
                  수정
                </button>
              )}
              {!isAuthor && !isMember && !study.isClosed && study.currentNumber < study.recruitNumber && (
                <button
                  onClick={handleJoinStudy}
                  className="bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700"
                >
                  참여신청
                </button>
              )}
              {!isAuthor && isMember && (
                <button
                  onClick={handleLeaveStudy}
                  className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700"
                >
                  탈퇴
                </button>
              )}
            </div>
          </div>
          
          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="flex justify-between items-start mb-4">
              <h1 className="text-3xl font-bold text-gray-900">{study.title}</h1>
              {getStatusBadge(study)}
            </div>
            
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
              <div className="flex items-center text-sm text-gray-500">
                <span className="font-medium mr-2">분류:</span>
                <span className="bg-blue-100 text-blue-800 px-2 py-1 rounded text-xs">
                  {study.category}
                </span>
              </div>
              <div className="flex items-center text-sm text-gray-500">
                <span className="font-medium mr-2">모드:</span>
                <span className="bg-green-100 text-green-800 px-2 py-1 rounded text-xs">
                  {study.mode}
                </span>
              </div>
              {study.location && (
                <div className="flex items-center text-sm text-gray-500">
                  <span className="font-medium mr-2">위치:</span>
                  <span>{study.location}</span>
                </div>
              )}
              <div className="flex items-center text-sm text-gray-500">
                <span className="font-medium mr-2">인원:</span>
                <span>{study.currentNumber || 1}/{study.recruitNumber}명</span>
              </div>
              <div className="flex items-center text-sm text-gray-500">
                <span className="font-medium mr-2">작성자:</span>
                <span>{study.author?.username || '알 수 없음'}</span>
              </div>
              <div className="flex items-center text-sm text-gray-500">
                <span className="font-medium mr-2">작성일:</span>
                <span>{formatDate(study.createdAt)}</span>
              </div>
            </div>
          </div>
        </div>

        {/* 탭 네비게이션 */}
        <div className="bg-white rounded-lg shadow-md mb-6">
          <div className="border-b border-gray-200">
            <nav className="-mb-px flex space-x-8 px-6">
              <button
                onClick={() => setActiveTab('info')}
                className={`py-4 px-1 border-b-2 font-medium text-sm ${
                  activeTab === 'info'
                    ? 'border-indigo-500 text-indigo-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                스터디 정보
              </button>
              <button
                onClick={() => setActiveTab('sessions')}
                className={`py-4 px-1 border-b-2 font-medium text-sm ${
                  activeTab === 'sessions'
                    ? 'border-indigo-500 text-indigo-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                스터디 세션 ({sessions.length})
              </button>
              <button
                onClick={() => setActiveTab('members')}
                className={`py-4 px-1 border-b-2 font-medium text-sm ${
                  activeTab === 'members'
                    ? 'border-indigo-500 text-indigo-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                멤버 ({members.length})
              </button>
            </nav>
          </div>

          <div className="p-6">
            {/* 스터디 정보 탭 */}
            {activeTab === 'info' && (
              <div>
                <h3 className="text-lg font-medium text-gray-900 mb-4">스터디 설명</h3>
                <div className="prose max-w-none">
                  <p className="text-gray-700 whitespace-pre-wrap">{study.description}</p>
                </div>
                
                {(study.studyStartDate || study.studyEndDate) && (
                  <div className="mt-6">
                    <h4 className="text-md font-medium text-gray-900 mb-2">스터디 기간</h4>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      {study.studyStartDate && (
                        <div>
                          <span className="text-sm font-medium text-gray-500">시작일:</span>
                          <span className="ml-2 text-sm text-gray-900">{formatDate(study.studyStartDate)}</span>
                        </div>
                      )}
                      {study.studyEndDate && (
                        <div>
                          <span className="text-sm font-medium text-gray-500">종료일:</span>
                          <span className="ml-2 text-sm text-gray-900">{formatDate(study.studyEndDate)}</span>
                        </div>
                      )}
                    </div>
                  </div>
                )}
              </div>
            )}

            {/* 스터디 세션 탭 */}
            {activeTab === 'sessions' && (
              <div>
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-lg font-medium text-gray-900">스터디 세션</h3>
                  {isAuthor && (
                    <button
                      onClick={() => navigate(`/studies/${id}/sessions/create`)}
                      className="bg-indigo-600 text-white px-3 py-1 rounded-md hover:bg-indigo-700 text-sm"
                    >
                      세션 추가
                    </button>
                  )}
                </div>
                
                {sessions.length === 0 ? (
                  <p className="text-gray-500">등록된 스터디 세션이 없습니다.</p>
                ) : (
                  <div className="space-y-4">
                    {sessions.map((session) => (
                      <div key={session.id} className="border border-gray-200 rounded-lg p-4">
                        <div className="flex justify-between items-start">
                          <div>
                            <h4 className="font-medium text-gray-900">
                              {session.title || `${session.sessionNumber}회차`}
                            </h4>
                            {session.description && (
                              <p className="text-sm text-gray-600 mt-1">{session.description}</p>
                            )}
                            <div className="mt-2 text-sm text-gray-500">
                              <span>일시: {formatDateTime(session.scheduledDate)}</span>
                            </div>
                          </div>
                          <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                            session.status === 'SCHEDULED' ? 'bg-blue-100 text-blue-800' :
                            session.status === 'IN_PROGRESS' ? 'bg-green-100 text-green-800' :
                            'bg-gray-100 text-gray-800'
                          }`}>
                            {session.status === 'SCHEDULED' ? '예정' :
                             session.status === 'IN_PROGRESS' ? '진행중' : '완료'}
                          </span>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}

            {/* 멤버 탭 */}
            {activeTab === 'members' && (
              <div>
                <h3 className="text-lg font-medium text-gray-900 mb-4">스터디 멤버</h3>
                
                {members.length === 0 ? (
                  <p className="text-gray-500">등록된 멤버가 없습니다.</p>
                ) : (
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {members.map((member) => (
                      <div key={member.id} className="flex items-center space-x-3 p-3 border border-gray-200 rounded-lg">
                        <div className="h-10 w-10 bg-indigo-600 rounded-full flex items-center justify-center">
                          <span className="text-white text-sm font-medium">
                            {member.user?.username?.charAt(0).toUpperCase()}
                          </span>
                        </div>
                        <div>
                          <p className="text-sm font-medium text-gray-900">{member.user?.username}</p>
                          <p className="text-xs text-gray-500">{member.user?.email}</p>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

const ProtectedRoute = ({ children }) => {
  const { user, loading } = useAuth();

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  if (!user) {
    return <Navigate to="/login" replace />;
  }

  return <>{children}</>;
};

const Layout = ({ children }) => {
  const { user } = useAuth();
  return (
    <div className="min-h-screen bg-gray-50">
      {user && <Header />}
      <main>{children}</main>
    </div>
  );
};

const App = () => {
  return (
    <QueryClientProvider client={queryClient}>
      <Router>
        <AuthProvider>
          <Layout>
            <Routes>
              <Route path="/" element={<ProtectedRoute><Home /></ProtectedRoute>} />
              <Route path="/login" element={<LoginForm />} />
              <Route path="/register" element={<RegisterForm />} />
              <Route path="/me" element={<ProtectedRoute><UserProfile /></ProtectedRoute>} />
              <Route path="/studies" element={<ProtectedRoute><StudyList /></ProtectedRoute>} />
              <Route path="/studies/:id" element={<ProtectedRoute><StudyDetail /></ProtectedRoute>} />
              <Route path="/create-study" element={<ProtectedRoute><CreateStudy /></ProtectedRoute>} />
              <Route path="*" element={<Navigate to="/" replace />} />
            </Routes>
          </Layout>
          <Toaster position="top-right" />
        </AuthProvider>
      </Router>
    </QueryClientProvider>
  );
};

export default App;
