const StudyList = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [studies, setStudies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");
  const [selectedMode, setSelectedMode] = useState("");
  const [selectedStatus, setSelectedStatus] = useState("");
  const [selectedLocation, setSelectedLocation] = useState("");
  const [minMembers, setMinMembers] = useState("");
  const [maxMembers, setMaxMembers] = useState("");
  const [showAdvancedFilters, setShowAdvancedFilters] = useState(false);
  const [searchHistory, setSearchHistory] = useState([]);
  const [showSearchHistory, setShowSearchHistory] = useState(false);
  const [debouncedSearchTerm, setDebouncedSearchTerm] = useState("");
  const [sortBy, setSortBy] = useState("createdAt");
  const [sortOrder, setSortOrder] = useState("desc");
  const [savedSearches, setSavedSearches] = useState([]);
  const [showSavedSearches, setShowSavedSearches] = useState(false);

  useEffect(() => {
    fetchStudies();
    // Load search history from localStorage
    const savedHistory = localStorage.getItem("studySearchHistory");
    if (savedHistory) {
      setSearchHistory(JSON.parse(savedHistory));
    }

    // Load search preferences from localStorage
    const savedPreferences = localStorage.getItem("studySearchPreferences");
    if (savedPreferences) {
      const preferences = JSON.parse(savedPreferences);
      setSelectedCategory(preferences.category || "");
      setSelectedMode(preferences.mode || "");
      setSelectedStatus(preferences.status || "");
      setSortBy(preferences.sortBy || "createdAt");
      setSortOrder(preferences.sortOrder || "desc");
    }

    // Load saved searches from localStorage
    const savedSearchesData = localStorage.getItem("savedSearches");
    if (savedSearchesData) {
      setSavedSearches(JSON.parse(savedSearchesData));
    }
  }, []);

  // Debounce search term
  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedSearchTerm(searchTerm);
    }, 300);

    return () => clearTimeout(timer);
  }, [searchTerm]);

  const saveSearchHistory = (term) => {
    if (!term.trim()) return;

    const newHistory = [
      term,
      ...searchHistory.filter((item) => item !== term),
    ].slice(0, 10);
    setSearchHistory(newHistory);
    localStorage.setItem("studySearchHistory", JSON.stringify(newHistory));
  };

  const clearSearchHistory = () => {
    setSearchHistory([]);
    localStorage.removeItem("studySearchHistory");
  };

  const saveSearchPreferences = () => {
    const preferences = {
      category: selectedCategory,
      mode: selectedMode,
      status: selectedStatus,
      sortBy,
      sortOrder,
    };
    localStorage.setItem("studySearchPreferences", JSON.stringify(preferences));
  };

  // Save preferences when they change
  useEffect(() => {
    saveSearchPreferences();
  }, [selectedCategory, selectedMode, selectedStatus, sortBy, sortOrder]);

  const saveCurrentSearch = () => {
    const searchName = prompt("검색 조건의 이름을 입력하세요:");
    if (!searchName) return;

    const currentSearch = {
      id: Date.now(),
      name: searchName,
      searchTerm,
      category: selectedCategory,
      mode: selectedMode,
      status: selectedStatus,
      location: selectedLocation,
      minMembers,
      maxMembers,
      sortBy,
      sortOrder,
      createdAt: new Date().toISOString(),
    };

    const newSavedSearches = [
      currentSearch,
      ...savedSearches.filter((s) => s.name !== searchName),
    ].slice(0, 5);
    setSavedSearches(newSavedSearches);
    localStorage.setItem("savedSearches", JSON.stringify(newSavedSearches));
  };

  const loadSavedSearch = (savedSearch) => {
    setSearchTerm(savedSearch.searchTerm || "");
    setSelectedCategory(savedSearch.category || "");
    setSelectedMode(savedSearch.mode || "");
    setSelectedStatus(savedSearch.status || "");
    setSelectedLocation(savedSearch.location || "");
    setMinMembers(savedSearch.minMembers || "");
    setMaxMembers(savedSearch.maxMembers || "");
    setSortBy(savedSearch.sortBy || "createdAt");
    setSortOrder(savedSearch.sortOrder || "desc");
    setShowSavedSearches(false);
  };

  const deleteSavedSearch = (searchId) => {
    const newSavedSearches = savedSearches.filter((s) => s.id !== searchId);
    setSavedSearches(newSavedSearches);
    localStorage.setItem("savedSearches", JSON.stringify(newSavedSearches));
  };

  const fetchStudies = async () => {
    try {
      setLoading(true);
      const response = await fetch("http://localhost:3001/api/post", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        setStudies(data.data || []);
      } else {
        setError("스터디 목록을 불러오는데 실패했습니다.");
      }
    } catch (error) {
      console.error("Fetch studies error:", error);
      setError("스터디 목록을 불러오는 중 오류가 발생했습니다.");
    } finally {
      setLoading(false);
    }
  };

  const filteredStudies = studies
    .filter((study) => {
      const matchesSearch =
        !debouncedSearchTerm ||
        study.title.toLowerCase().includes(debouncedSearchTerm.toLowerCase()) ||
        study.description
          .toLowerCase()
          .includes(debouncedSearchTerm.toLowerCase()) ||
        (study.author?.username &&
          study.author.username
            .toLowerCase()
            .includes(debouncedSearchTerm.toLowerCase()));

      const matchesCategory =
        !selectedCategory || study.category === selectedCategory;
      const matchesMode = !selectedMode || study.mode === selectedMode;
      const matchesLocation =
        !selectedLocation ||
        (study.location &&
          study.location
            .toLowerCase()
            .includes(selectedLocation.toLowerCase()));

      // Status filtering
      let matchesStatus = true;
      if (selectedStatus === "recruiting") {
        matchesStatus =
          !study.isClosed && study.currentNumber < study.recruitNumber;
      } else if (selectedStatus === "full") {
        matchesStatus = study.currentNumber >= study.recruitNumber;
      } else if (selectedStatus === "closed") {
        matchesStatus = study.isClosed;
      }

      // Member count filtering
      const matchesMinMembers =
        !minMembers || study.recruitNumber >= parseInt(minMembers);
      const matchesMaxMembers =
        !maxMembers || study.recruitNumber <= parseInt(maxMembers);

      return (
        matchesSearch &&
        matchesCategory &&
        matchesMode &&
        matchesStatus &&
        matchesLocation &&
        matchesMinMembers &&
        matchesMaxMembers
      );
    })
    .sort((a, b) => {
      let aValue, bValue;

      switch (sortBy) {
        case "title":
          aValue = a.title.toLowerCase();
          bValue = b.title.toLowerCase();
          break;
        case "category":
          aValue = a.category.toLowerCase();
          bValue = b.category.toLowerCase();
          break;
        case "recruitNumber":
          aValue = a.recruitNumber;
          bValue = b.recruitNumber;
          break;
        case "currentNumber":
          aValue = a.currentNumber || 0;
          bValue = b.currentNumber || 0;
          break;
        case "createdAt":
        default:
          aValue = new Date(a.createdAt);
          bValue = new Date(b.createdAt);
          break;
      }

      if (sortOrder === "asc") {
        return aValue > bValue ? 1 : -1;
      } else {
        return aValue < bValue ? 1 : -1;
      }
    });

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    saveSearchHistory(searchTerm);
    setShowSearchHistory(false);
  };

  const handleSearchHistoryClick = (term) => {
    setSearchTerm(term);
    setShowSearchHistory(false);
  };

  // Highlight search terms in text
  const highlightText = (text, searchTerm) => {
    if (!searchTerm || !text) return text;

    const regex = new RegExp(`(${searchTerm})`, "gi");
    const parts = text.split(regex);

    return parts.map((part, index) =>
      regex.test(part) ? (
        <mark key={index} className="bg-yellow-200 px-1 rounded">
          {part}
        </mark>
      ) : (
        part
      ),
    );
  };

  // Close search history when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (showSearchHistory && !event.target.closest(".search-container")) {
        setShowSearchHistory(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [showSearchHistory]);

  const formatDate = (dateString) => {
    if (!dateString) return "미정";
    return new Date(dateString).toLocaleDateString("ko-KR");
  };

  const getStatusBadge = (study) => {
    if (study.isClosed) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">
          모집 완료
        </span>
      );
    }
    if (study.currentNumber >= study.recruitNumber) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">
          인원 마감
        </span>
      );
    }
    return (
      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
        모집 중
      </span>
    );
  };

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="flex justify-center">
            <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-indigo-600"></div>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="text-center">
            <h2 className="text-2xl font-bold text-gray-900 mb-4">
              오류가 발생했습니다
            </h2>
            <p className="text-gray-600 mb-4">{error}</p>
            <button
              onClick={fetchStudies}
              className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700"
            >
              다시 시도
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900">스터디 목록</h1>
          <button
            onClick={() => navigate("/create-study")}
            className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700"
          >
            새 스터디 만들기
          </button>
        </div>

        {/* 검색 및 필터 */}
        <div className="bg-white p-6 rounded-lg shadow mb-6">
          {/* 기본 검색 및 필터 */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-4">
            <div className="relative search-container">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                검색
              </label>
              <form onSubmit={handleSearchSubmit} className="relative">
                <input
                  type="text"
                  placeholder="스터디 제목, 내용, 작성자로 검색"
                  value={searchTerm}
                  onChange={(e) => {
                    setSearchTerm(e.target.value);
                    setShowSearchHistory(true);
                  }}
                  onFocus={() => setShowSearchHistory(true)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                />
                <button
                  type="submit"
                  className="absolute right-2 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
                >
                  <svg
                    className="w-5 h-5"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
                    />
                  </svg>
                </button>
              </form>

              {/* 검색 기록 드롭다운 */}
              {showSearchHistory && searchHistory.length > 0 && (
                <div className="absolute z-10 w-full mt-1 bg-white border border-gray-300 rounded-md shadow-lg">
                  <div className="p-2 border-b border-gray-200 flex justify-between items-center">
                    <span className="text-sm font-medium text-gray-700">
                      최근 검색어
                    </span>
                    <button
                      onClick={clearSearchHistory}
                      className="text-xs text-red-600 hover:text-red-800"
                    >
                      전체 삭제
                    </button>
                  </div>
                  {searchHistory.map((term, index) => (
                    <button
                      key={index}
                      onClick={() => handleSearchHistoryClick(term)}
                      className="w-full text-left px-3 py-2 text-sm text-gray-700 hover:bg-gray-100 flex items-center"
                    >
                      <svg
                        className="w-4 h-4 mr-2 text-gray-400"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z"
                        />
                      </svg>
                      {term}
                    </button>
                  ))}
                </div>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                분류
              </label>
              <select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
