const MyStudies = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [studies, setStudies] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // user가 null인 경우 로딩 상태 표시
  if (!user) {
    return (
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="flex justify-center">
            <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-indigo-600"></div>
          </div>
        </div>
      </div>
    );
  }

  const fetchMyStudies = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");

      const response = await fetch(
        "http://localhost:3001/api/post/user/my-studies",
        {
          headers: { Authorization: `Bearer ${token}` },
        },
      );

      if (response.ok) {
        const data = await response.json();
        setStudies(data);
      } else {
        setError("스터디 목록을 불러오는데 실패했습니다.");
      }
    } catch (error) {
      console.error("Failed to fetch my studies:", error);
      setError("스터디 목록을 불러오는 중 오류가 발생했습니다.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (user) {
      fetchMyStudies();
    }
  }, [user]);

  const formatDate = (dateString) => {
    if (!dateString) return "미정";
    return new Date(dateString).toLocaleDateString("ko-KR");
  };

  const getStatusBadge = (study) => {
    if (study?.isClosed) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">
          모집 완료
        </span>
      );
    }
    if (study?.currentNumber >= study?.recruitNumber) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">
          인원 마감
        </span>
      );
    }
    return (
      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
        모집 중
      </span>
    );
  };

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="flex justify-center">
            <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-indigo-600"></div>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="text-center">
            <h2 className="text-2xl font-bold text-gray-900 mb-4">
              오류가 발생했습니다
            </h2>
            <p className="text-gray-600 mb-4">{error}</p>
            <button
              onClick={fetchMyStudies}
              className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700"
            >
              다시 시도
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        <div className="flex items-center justify-between mb-8">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">
              내가 만든 스터디
            </h1>
            <p className="text-gray-600 mt-2">
              총{" "}
              {Array.isArray(studies?.data?.data)
                ? studies.data.data.length
                : 0}
              개의 스터디
            </p>
          </div>
          <button
            onClick={() => navigate("/create-study")}
            className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700"
          >
            새 스터디 만들기
          </button>
        </div>

        {!Array.isArray(studies?.data?.data) ||
        studies?.data?.data?.length === 0 ? (
          <div className="text-center py-12">
            <div className="text-6xl mb-4">👑</div>
            <h2 className="text-2xl font-bold text-gray-900 mb-2">
              아직 만든 스터디가 없습니다
            </h2>
            <p className="text-gray-600 mb-6">첫 번째 스터디를 만들어보세요!</p>
            <button
              onClick={() => navigate("/create-study")}
