  console.log("URL ID type:", typeof id);
  console.log("Current URL:", window.location.href);

  useEffect(() => {
    if (id) {
      fetchStudyDetail();
      fetchStudySessions();
      fetchStudyMembers();
    }
  }, [id]);

  const fetchStudyDetail = async () => {
    try {
      setLoading(true);
      console.log("Fetching study detail for ID:", id);
      console.log("Fetching study detail for ID type:", typeof id);
      console.log("API URL:", `http://localhost:3001/api/post/${id}/detail`);
      const token = localStorage.getItem("token");
      console.log("Token for study detail:", token);

      const response = await fetch(
        `http://localhost:3001/api/post/${id}/detail`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        },
      );

      console.log("Study detail response status:", response.status);

