                }`}
              >
                스터디 정보
              </button>
              <button
                onClick={() => setActiveTab("sessions")}
                className={`py-4 px-1 border-b-2 font-medium text-sm ${
                  activeTab === "sessions"
                    ? "border-indigo-500 text-indigo-600"
                    : "border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300"
                }`}
              >
                스터디 세션 ({sessions.length})
              </button>
              <button
                onClick={() => setActiveTab("members")}
                className={`py-4 px-1 border-b-2 font-medium text-sm ${
                  activeTab === "members"
                    ? "border-indigo-500 text-indigo-600"
                    : "border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300"
                }`}
              >
                멤버 ({members.length})
              </button>
            </nav>
          </div>

          <div className="p-6">
            {/* 스터디 정보 탭 */}
            {activeTab === "info" && (
              <div>
                <h3 className="text-lg font-medium text-gray-900 mb-4">
                  스터디 설명
                </h3>
                <div className="prose max-w-none">
                  <p className="text-gray-700 whitespace-pre-wrap">
                    {study.description}
                  </p>
                </div>

                {(study.studyStartDate || study.studyEndDate) && (
                  <div className="mt-6">
                    <h4 className="text-md font-medium text-gray-900 mb-2">
                      스터디 기간
                    </h4>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      {study.studyStartDate && (
                        <div>
                          <span className="text-sm font-medium text-gray-500">
                            시작일:
                          </span>
                          <span className="ml-2 text-sm text-gray-900">
                            {formatDate(study.studyStartDate)}
                          </span>
                        </div>
                      )}
                      {study.studyEndDate && (
                        <div>
                          <span className="text-sm font-medium text-gray-500">
                            종료일:
                          </span>
                          <span className="ml-2 text-sm text-gray-900">
                            {formatDate(study.studyEndDate)}
                          </span>
                        </div>
                      )}
                    </div>
                  </div>
                )}
              </div>
            )}

            {/* 스터디 세션 탭 */}
            {activeTab === "sessions" && (
              <div>
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-lg font-medium text-gray-900">
                    스터디 세션
                  </h3>
                  {isAuthor && (
                    <button
                      onClick={() => navigate(`/studies/${id}/sessions/create`)}
                      className="bg-indigo-600 text-white px-3 py-1 rounded-md hover:bg-indigo-700 text-sm"
                    >
                      세션 추가
                    </button>
                  )}
                </div>

                {sessions.length === 0 ? (
                  <p className="text-gray-500">
                    등록된 스터디 세션이 없습니다.
                  </p>
                ) : (
                  <div className="space-y-4">
                    {sessions.map((session) => (
                      <div
                        key={session.id}
                        className="border border-gray-200 rounded-lg p-4"
                      >
                        <div className="flex justify-between items-start">
                          <div>
                            <h4 className="font-medium text-gray-900">
                              {session.title || `${session.sessionNumber}회차`}
                            </h4>
                            {session.description && (
                              <p className="text-sm text-gray-600 mt-1">
                                {session.description}
                              </p>
                            )}
                            <div className="mt-2 text-sm text-gray-500">
                              <span>
                                일시: {formatDateTime(session.scheduledDate)}
                              </span>
                            </div>
                          </div>
                          <span
                            className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                              session.status === "SCHEDULED"
                                ? "bg-blue-100 text-blue-800"
                                : session.status === "IN_PROGRESS"
                                  ? "bg-green-100 text-green-800"
                                  : "bg-gray-100 text-gray-800"
                            }`}
                          >
                            {session.status === "SCHEDULED"
                              ? "예정"
                              : session.status === "IN_PROGRESS"
                                ? "진행중"
                                : "완료"}
                          </span>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}

            {/* 멤버 탭 */}
            {activeTab === "members" && (
              <div>
                <h3 className="text-lg font-medium text-gray-900 mb-4">
                  스터디 멤버
                </h3>

                {members.length === 0 ? (
                  <p className="text-gray-500">등록된 멤버가 없습니다.</p>
                ) : (
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {members.map((member) => (
                      <div
                        key={member.id}
                        className="flex items-center space-x-3 p-3 border border-gray-200 rounded-lg"
                      >
                        <div className="h-10 w-10 bg-indigo-600 rounded-full flex items-center justify-center">
                          <span className="text-white text-sm font-medium">
                            {member.user?.username?.charAt(0).toUpperCase()}
                          </span>
                        </div>
                        <div>
                          <p className="text-sm font-medium text-gray-900">
                            {member.user?.username}
                          </p>
                          <p className="text-xs text-gray-500">
                            {member.user?.email}
                          </p>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* ApplicationForm 모달 */}
      {showApplicationForm && study?.applicationFormTemplate && (
        <div>
          {console.log("Rendering ApplicationForm modal")}
          {console.log("showApplicationForm:", showApplicationForm)}
          {console.log(
            "study.applicationFormTemplate:",
            study.applicationFormTemplate,
          )}
          <ApplicationForm
            questions={study.applicationFormTemplate}
            onSubmit={handleJoinStudy}
            onCancel={() => setShowApplicationForm(false)}
            loading={false}
          />
        </div>
      )}
    </div>
  );
};

const ProtectedRoute = ({ children }) => {
  const { user, loading } = useAuth();

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  if (!user) {
    return <Navigate to="/login" replace />;
  }

  return <>{children}</>;
};

const Layout = ({ children }) => {
  const { user } = useAuth();
  return (
    <div className="min-h-screen bg-gray-50">
      {user && <Header />}
      <main>{children}</main>
    </div>
  );
};

const App = () => {
  return (
    <QueryClientProvider client={queryClient}>
      <Router>
        <AuthProvider>
          <Layout>
            <Routes>
              <Route
                path="/"
                element={
                  <ProtectedRoute>
                    <Home />
                  </ProtectedRoute>
                }
              />
              <Route path="/login" element={<LoginForm />} />
              <Route path="/register" element={<RegisterForm />} />
              <Route
                path="/me"
                element={
                  <ProtectedRoute>
                    <UserProfile />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/studies"
                element={
                  <ProtectedRoute>
                    <StudyList />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/studies/:id"
                element={
                  <ProtectedRoute>
                    <StudyDetail />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/my-studies"
                element={
                  <ProtectedRoute>
                    <MyStudies />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/my-applications"
                element={
                  <ProtectedRoute>
                    <MyApplications />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/my-memberships"
                element={
                  <ProtectedRoute>
                    <MyMemberships />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/my-member-studies"
                element={
                  <ProtectedRoute>
                    <MyMemberStudies />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/my-leader-studies"
                element={
                  <ProtectedRoute>
                    <MyLeaderStudies />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/create-study"
                element={
                  <ProtectedRoute>
                    <CreateStudy />
                  </ProtectedRoute>
                }
              />
              <Route path="*" element={<Navigate to="/" replace />} />
            </Routes>
          </Layout>
          <Toaster position="top-right" />
        </AuthProvider>
      </Router>
    </QueryClientProvider>
  );
};

export default App;
