import React, { useState, useEffect, createContext, useContext } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
  Link,
  useNavigate,
  useParams,
} from "react-router-dom";
import { QueryClient, QueryClientProvider, useQuery, useMutation, useQueryClient } from "react-query";
import { Toaster, toast } from "react-hot-toast";
import StudyApplicants from "./pages/StudyApplicants";
import { GOOGLE_CLIENT_ID } from "./config";

// Google OAuth 타입 정의
declare global {
  interface Window {
    google: {
      accounts: {
        id: {
          initialize: (config: any) => void;
          prompt: () => void;
        };
      };
    };
  }
}

// Add basic types to fix TypeScript errors
interface User {
  id: number;
  username: string;
  email: string;
}

interface AuthContextType {
  user: User | null;
  loading: boolean;
  login: (email: string, password: string) => Promise<void>;
  loginWithGoogle: (credential: string) => Promise<void>;
  register: (email: string, username: string, password: string) => Promise<void>;
  registerWithGoogle: (credential: string) => Promise<void>;
  logout: () => void;
}

const queryClient = new QueryClient();

const AuthContext = createContext<AuthContextType | undefined>(undefined);

const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};

const AuthProvider = ({ children }: { children: React.ReactNode }) => {
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    console.log("useEffect - Token found:", !!token);
    if (token) {
      console.log("useEffect - Calling fetchCurrentUser");
      fetchCurrentUser();
    } else {
      setLoading(false);
    }
  }, []);

  const fetchCurrentUser = async () => {
    try {
      const token = localStorage.getItem("token");
      console.log("Token from localStorage:", token);
      console.log("Token type:", typeof token);
      console.log("Token length:", token ? token.length : 0);

      if (!token) {
        console.log("No token found in localStorage");
        setLoading(false);
        return;
      }

      const response = await fetch("http://localhost:3001/api/user/me", {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log("User fetch response status:", response.status);
      if (response.ok) {
        const data = await response.json();
        console.log("User data received:", data);
        console.log("Setting user with data.data:", data.data);
        console.log("data.data.username:", data.data?.username);
        setUser(data.data);
      } else {
        console.log("User fetch failed, removing token");
        localStorage.removeItem("token");
      }
    } catch (error) {
      console.error("Failed to fetch user:", error);
      localStorage.removeItem("token");
    } finally {
      setLoading(false);
    }
  };

  const login = async (email, password) => {
    setLoading(true);
    try {
      const response = await fetch("http://localhost:3001/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });
      const data = await response.json();
      console.log("Login response:", data);
      console.log("Response status:", response.status);
      console.log("Response ok:", response.ok);
      console.log("Data keys:", Object.keys(data));
      if (response.ok) {
        // 응답 구조에 따라 토큰 추출
        const token = data.token || (data.data && data.data.token);
        console.log("Token received:", token);
        console.log("Token type:", typeof token);
        console.log("Token length:", token ? token.length : 0);

        if (!token) {
          console.error("No token found in response:", data);
          toast.error("토큰을 받지 못했습니다.");
          return;
        }

        localStorage.setItem("token", token);
        console.log("Token stored in localStorage");

        // 저장된 토큰 확인
        const storedToken = localStorage.getItem("token");
        console.log("Stored token verification:", storedToken);

        await fetchCurrentUser();
        toast.success("로그인 성공!");
        navigate("/me");
      } else {
        toast.error(data.message || "로그인 실패");
      }
    } catch (error) {
      console.error("Login error:", error);
      toast.error("로그인 중 오류가 발생했습니다.");
    } finally {
      setLoading(false);
    }
  };

  const loginWithGoogle = async (credential: string) => {
    setLoading(true);
    try {
      const response = await fetch("http://localhost:3001/api/auth/google", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ credential }),
      });
      const data = await response.json();

      if (response.ok) {
        const token = data.token || (data.data && data.data.token);

        if (!token) {
          console.error("No token found in Google login response:", data);
          toast.error("Google 로그인 중 토큰을 받지 못했습니다.");
          return;
        }

        localStorage.setItem("token", token);
        await fetchCurrentUser();
        toast.success("Google 로그인 성공!");
        navigate("/me");
      } else {
        toast.error(data.message || "Google 로그인 실패");
      }
    } catch (error) {
      console.error("Google login error:", error);
      toast.error("Google 로그인 중 오류가 발생했습니다.");
    } finally {
      setLoading(false);
    }
  };

  const registerWithGoogle = async (credential: string) => {
    setLoading(true);
    try {
      const response = await fetch("http://localhost:3001/api/auth/google/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ credential }),
      });
      const data = await response.json();

      if (response.ok) {
        const token = data.token || (data.data && data.data.token);

        if (!token) {
          console.error("No token found in Google registration response:", data);
          toast.error("Google 회원가입 중 토큰을 받지 못했습니다.");
          return;
        }

        localStorage.setItem("token", token);
        await fetchCurrentUser();
        toast.success("Google 회원가입 성공!");
        navigate("/me");
      } else {
        toast.error(data.message || "Google 회원가입 실패");
      }
    } catch (error) {
      console.error("Google registration error:", error);
      toast.error("Google 회원가입 중 오류가 발생했습니다.");
    } finally {
      setLoading(false);
    }
  };

  const register = async (email, username, password) => {
    setLoading(true);
    try {
      const response = await fetch("http://localhost:3001/api/auth/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, username, password }),
      });
      const data = await response.json();
      if (response.ok) {
        toast.success("회원가입 성공! 로그인해주세요.");
        navigate("/login");
      } else {
        toast.error(data.message || "회원가입 실패");
      }
    } catch (error) {
      toast.error("회원가입 중 오류가 발생했습니다.");
    } finally {
      setLoading(false);
    }
  };

  const logout = () => {
    localStorage.removeItem("token");
    setUser(null);
    toast.success("로그아웃되었습니다.");
    navigate("/login");
  };

  return (
    <AuthContext.Provider
      value={{ user, loading, login, loginWithGoogle, register, registerWithGoogle, logout, fetchCurrentUser }}
    >
      {children}
    </AuthContext.Provider>
  );
};

