const CreateStudy = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    category: "",
    mode: "",
    location: "",
    recruitNumber: 1,
    studyStartDate: "",
    studyEndDate: "",
  });
  const [errors, setErrors] = useState({});

  // Request form state
  const [showRequestForm, setShowRequestForm] = useState(false);
  const [requestFormData, setRequestFormData] = useState({
    title: "스터디 지원 양식",
    questions: [],
  });
  const [requestFormErrors, setRequestFormErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};
    if (!formData.title.trim()) newErrors.title = "스터디 제목을 입력해주세요.";
    if (!formData.description.trim())
      newErrors.description = "스터디 내용을 입력해주세요.";
    if (!formData.category) newErrors.category = "스터디 분류를 선택해주세요.";
    if (!formData.mode) newErrors.mode = "스터디 모드를 선택해주세요.";
    if (formData.recruitNumber < 1)
      newErrors.recruitNumber = "모집 인원은 1명 이상이어야 합니다.";
    if (
      formData.studyStartDate &&
      formData.studyEndDate &&
      new Date(formData.studyStartDate) > new Date(formData.studyEndDate)
    ) {
      newErrors.studyEndDate = "종료일은 시작일보다 늦어야 합니다.";
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateRequestForm = () => {
    const newErrors = {};
    if (!requestFormData.title.trim())
      newErrors.title = "양식 제목을 입력해주세요.";
    if (requestFormData.questions.length === 0)
      newErrors.questions = "최소 하나의 질문을 추가해주세요.";

    // Validate each question
    requestFormData.questions.forEach((question, index) => {
      if (!question.questionText.trim()) {
        newErrors[`question_${index}`] = "질문 내용을 입력해주세요.";
      }

      // 체크박스나 객관식 선택형인 경우 옵션이 필요
      if (
        (question.type === "checkbox" || question.type === "multiple_choice") &&
        (!question.options || question.options.length === 0)
      ) {
        newErrors[`question_${index}_options`] =
          `${question.type === "checkbox" ? "체크박스" : "객관식"} 옵션을 최소 하나 추가해주세요.`;
      }

      // 옵션이 있는 경우 빈 옵션이 있는지 확인
      if (question.options && question.options.length > 0) {
        const emptyOptions = question.options.filter(
          (option) => !option.trim(),
        );
        if (emptyOptions.length > 0) {
          newErrors[`question_${index}_options`] =
            "빈 옵션이 있습니다. 모든 옵션을 입력해주세요.";
        }
      }
    });

    setRequestFormErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    // Validate request form if it's enabled
    if (showRequestForm && !validateRequestForm()) return;

    setLoading(true);
    try {
      // First, create the study
      const studyResponse = await fetch("http://localhost:3001/api/post", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({
          ...formData,
          recruitNumber: parseInt(formData.recruitNumber),
          studyStartDate: formData.studyStartDate || null,
          studyEndDate: formData.studyEndDate || null,
        }),
      });

      const studyData = await studyResponse.json();

      if (studyResponse.ok) {
        // If request form is enabled, create it
        if (showRequestForm && requestFormData.questions.length > 0) {
          // Post가 완전히 저장될 때까지 잠시 대기
          await new Promise((resolve) => setTimeout(resolve, 1000));

          try {
            const formResponse = await fetch(
              "http://localhost:3001/api/requestform",
              {
                method: "POST",
                headers: {
                  "Content-Type": "application/json",
                  Authorization: `Bearer ${localStorage.getItem("token")}`,
                },
                body: JSON.stringify({
                  postId: studyData.data.id,
                  title: requestFormData.title,
                  questions: requestFormData.questions.map((q, index) => ({
                    questionText: q.questionText,
                    type: q.type,
                    isRequired: q.isRequired,
                    order: index + 1,
                    options: q.options || [],
                  })),
                }),
              },
            );

            if (formResponse.ok) {
              toast.success("스터디와 지원 양식이 성공적으로 생성되었습니다!");
            } else {
              toast.success(
                "스터디가 생성되었지만 지원 양식 생성에 실패했습니다.",
              );
            }
          } catch (formError) {
            console.error("Create request form error:", formError);
            toast.success(
              "스터디가 생성되었지만 지원 양식 생성에 실패했습니다.",
            );
          }
        } else {
          toast.success("스터디가 성공적으로 생성되었습니다!");
        }
        navigate("/studies");
      } else {
        toast.error(studyData.message || "스터디 생성에 실패했습니다.");
      }
    } catch (error) {
      console.error("Create study error:", error);
      toast.error("스터디 생성 중 오류가 발생했습니다.");
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    if (errors[name]) setErrors({ ...errors, [name]: "" });
  };

  // Request form helper functions
  const addQuestion = () => {
    setRequestFormData({
      ...requestFormData,
      questions: [
        ...requestFormData.questions,
        {
          questionText: "",
          type: "short_answer",
          isRequired: true,
          options: [],
        },
      ],
    });
  };

  const removeQuestion = (index) => {
    const newQuestions = requestFormData.questions.filter(
      (_, i) => i !== index,
    );
    setRequestFormData({
      ...requestFormData,
      questions: newQuestions,
    });
  };

  const updateQuestion = (index, field, value) => {
    const newQuestions = [...requestFormData.questions];
    newQuestions[index] = { ...newQuestions[index], [field]: value };
    setRequestFormData({
      ...requestFormData,
      questions: newQuestions,
    });
  };

  const handleRequestFormChange = (e) => {
    const { name, value } = e.target;
    setRequestFormData({ ...requestFormData, [name]: value });
    if (requestFormErrors[name])
      setRequestFormErrors({ ...requestFormErrors, [name]: "" });
  };

  const addOption = (questionIndex) => {
    const newQuestions = [...requestFormData.questions];
    if (!newQuestions[questionIndex].options) {
      newQuestions[questionIndex].options = [];
    }
    newQuestions[questionIndex].options.push("");
    setRequestFormData({
      ...requestFormData,
      questions: newQuestions,
    });
  };

  const removeOption = (questionIndex, optionIndex) => {
    const newQuestions = [...requestFormData.questions];
    newQuestions[questionIndex].options.splice(optionIndex, 1);
    setRequestFormData({
      ...requestFormData,
      questions: newQuestions,
    });
  };

  const updateOption = (questionIndex, optionIndex, value) => {
    const newQuestions = [...requestFormData.questions];
    newQuestions[questionIndex].options[optionIndex] = value;
    setRequestFormData({
      ...requestFormData,
      questions: newQuestions,
    });
  };

  return (
    <div className="max-w-4xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        <div className="bg-white shadow rounded-lg">
          <div className="px-4 py-5 sm:p-6">
            <h1 className="text-3xl font-bold text-gray-900 mb-8">
              새 스터디 만들기
            </h1>

            <form onSubmit={handleSubmit} className="space-y-6">
              <div className="grid grid-cols-1 gap-6 sm:grid-cols-2">
                <div className="sm:col-span-2">
                  <label
                    htmlFor="title"
                    className="block text-sm font-medium text-gray-700"
                  >
                    스터디 제목 *
                  </label>
                  <input
                    type="text"
                    id="title"
                    name="title"
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.title ? "border-red-500" : "border-gray-300"
                    }`}
                    value={formData.title}
                    onChange={handleChange}
                    placeholder="스터디 제목을 입력하세요"
                  />
                  {errors.title && (
                    <p className="mt-1 text-sm text-red-600">{errors.title}</p>
                  )}
                </div>

                <div className="sm:col-span-2">
                  <label
                    htmlFor="description"
                    className="block text-sm font-medium text-gray-700"
                  >
                    스터디 내용 *
                  </label>
                  <textarea
                    id="description"
                    name="description"
                    rows={4}
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.description ? "border-red-500" : "border-gray-300"
                    }`}
                    value={formData.description}
                    onChange={handleChange}
                    placeholder="스터디에 대한 자세한 설명을 입력하세요"
                  />
                  {errors.description && (
                    <p className="mt-1 text-sm text-red-600">
                      {errors.description}
                    </p>
                  )}
                </div>

                <div>
                  <label
                    htmlFor="category"
                    className="block text-sm font-medium text-gray-700"
                  >
                    스터디 분류 *
                  </label>
                  <select
                    id="category"
                    name="category"
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.category ? "border-red-500" : "border-gray-300"
                    }`}
                    value={formData.category}
                    onChange={handleChange}
                  >
                    <option value="">분류를 선택하세요</option>
                    <option value="프로그래밍">프로그래밍</option>
                    <option value="언어">언어</option>
                    <option value="자격증">자격증</option>
                    <option value="취업준비">취업준비</option>
                    <option value="기타">기타</option>
                  </select>
                  {errors.category && (
