const Home = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [myStudies, setMyStudies] = useState([]);
  const [myApplications, setMyApplications] = useState([]);
  const [myMemberships, setMyMemberships] = useState([]);
  const [myMemberStudies, setMyMemberStudies] = useState([]);
  const [myLeaderStudies, setMyLeaderStudies] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (user) {
      fetchUserStudies();
    }
  }, [user]);

  const fetchUserStudies = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");

      // 내가 만든 스터디 (post에서 author가 나인 것들)
      const myStudiesResponse = await fetch(
        "http://localhost:3001/api/post/user/my-studies",
        {
          headers: { Authorization: `Bearer ${token}` },
        },
      );
      if (myStudiesResponse.ok) {
        const myStudiesData = await myStudiesResponse.json();
        console.log("My studies data:", myStudiesData);
        setMyStudies(myStudiesData);
      }

      // 내가 지원한 스터디
      const applicationsResponse = await fetch(
        "http://localhost:3001/api/post/applications",
        {
          headers: { Authorization: `Bearer ${token}` },
        },
      );
      if (applicationsResponse.ok) {
        const applicationsData = await applicationsResponse.json();
        console.log("Applications data:", applicationsData);
        setMyApplications(applicationsData);
      }

      // 내가 멤버로 있는 스터디 (memberships)
      const membershipsResponse = await fetch(
        "http://localhost:3001/api/post/memberships",
        {
          headers: { Authorization: `Bearer ${token}` },
        },
      );
      if (membershipsResponse.ok) {
        const membershipsData = await membershipsResponse.json();
        console.log("Memberships data:", membershipsData);
        setMyMemberships(membershipsData);
      }

      // 내가 멤버로 참여하는 운영스터디 (member-studies)
      const memberStudiesResponse = await fetch(
        "http://localhost:3001/api/post/member-studies",
        {
          headers: { Authorization: `Bearer ${token}` },
        },
      );
      if (memberStudiesResponse.ok) {
        const memberStudiesData = await memberStudiesResponse.json();
        console.log("Member studies data:", memberStudiesData);
        setMyMemberStudies(memberStudiesData);
      }

      // 내가 리더로 있는 스터디
      const leaderStudiesResponse = await fetch(
        "http://localhost:3001/api/post/leader-studies",
        {
          headers: { Authorization: `Bearer ${token}` },
        },
      );
      if (leaderStudiesResponse.ok) {
        const leaderStudiesData = await leaderStudiesResponse.json();
        console.log("Leader studies data:", leaderStudiesData);
        setMyLeaderStudies(leaderStudiesData);
      }
    } catch (error) {
      console.error("Failed to fetch user studies:", error);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return "미정";
    return new Date(dateString).toLocaleDateString("ko-KR");
  };

  const getStatusBadge = (study) => {
    if (study?.isClosed) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">
          모집 완료
        </span>
      );
    }
    if (study?.currentNumber >= study?.recruitNumber) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">
          인원 마감
        </span>
      );
    }
    return (
      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
        모집 중
