const StudyList = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [studies, setStudies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");
  const [selectedMode, setSelectedMode] = useState("");
  const [selectedStatus, setSelectedStatus] = useState("");
  const [selectedLocation, setSelectedLocation] = useState("");
  const [minMembers, setMinMembers] = useState("");
  const [maxMembers, setMaxMembers] = useState("");
  const [showAdvancedFilters, setShowAdvancedFilters] = useState(false);
  const [searchHistory, setSearchHistory] = useState([]);
  const [showSearchHistory, setShowSearchHistory] = useState(false);
  const [debouncedSearchTerm, setDebouncedSearchTerm] = useState("");
  const [sortBy, setSortBy] = useState("createdAt");
  const [sortOrder, setSortOrder] = useState("desc");
  const [savedSearches, setSavedSearches] = useState([]);
  const [showSavedSearches, setShowSavedSearches] = useState(false);

  useEffect(() => {
    fetchStudies();
    // Load search history from localStorage
    const savedHistory = localStorage.getItem("studySearchHistory");
    if (savedHistory) {
      setSearchHistory(JSON.parse(savedHistory));
    }

    // Load search preferences from localStorage
    const savedPreferences = localStorage.getItem("studySearchPreferences");
    if (savedPreferences) {
      const preferences = JSON.parse(savedPreferences);
      setSelectedCategory(preferences.category || "");
      setSelectedMode(preferences.mode || "");
      setSelectedStatus(preferences.status || "");
      setSortBy(preferences.sortBy || "createdAt");
      setSortOrder(preferences.sortOrder || "desc");
    }

    // Load saved searches from localStorage
    const savedSearchesData = localStorage.getItem("savedSearches");
    if (savedSearchesData) {
      setSavedSearches(JSON.parse(savedSearchesData));
    }
  }, []);

  // Debounce search term
  useEffect(() => {
    const timer = setTimeout(() => {
      setDebouncedSearchTerm(searchTerm);
    }, 300);

    return () => clearTimeout(timer);
  }, [searchTerm]);

  const saveSearchHistory = (term) => {
    if (!term.trim()) return;

    const newHistory = [
      term,
      ...searchHistory.filter((item) => item !== term),
    ].slice(0, 10);
    setSearchHistory(newHistory);
    localStorage.setItem("studySearchHistory", JSON.stringify(newHistory));
  };

  const clearSearchHistory = () => {
    setSearchHistory([]);
    localStorage.removeItem("studySearchHistory");
  };

  const saveSearchPreferences = () => {
    const preferences = {
      category: selectedCategory,
      mode: selectedMode,
      status: selectedStatus,
      sortBy,
      sortOrder,
    };
    localStorage.setItem("studySearchPreferences", JSON.stringify(preferences));
  };

  // Save preferences when they change
  useEffect(() => {
    saveSearchPreferences();
  }, [selectedCategory, selectedMode, selectedStatus, sortBy, sortOrder]);

  const saveCurrentSearch = () => {
    const searchName = prompt("검색 조건의 이름을 입력하세요:");
    if (!searchName) return;

    const currentSearch = {
      id: Date.now(),
      name: searchName,
      searchTerm,
      category: selectedCategory,
      mode: selectedMode,
      status: selectedStatus,
      location: selectedLocation,
      minMembers,
      maxMembers,
      sortBy,
      sortOrder,
      createdAt: new Date().toISOString(),
    };

    const newSavedSearches = [
      currentSearch,
      ...savedSearches.filter((s) => s.name !== searchName),
    ].slice(0, 5);
    setSavedSearches(newSavedSearches);
    localStorage.setItem("savedSearches", JSON.stringify(newSavedSearches));
  };

  const loadSavedSearch = (savedSearch) => {
    setSearchTerm(savedSearch.searchTerm || "");
    setSelectedCategory(savedSearch.category || "");
    setSelectedMode(savedSearch.mode || "");
    setSelectedStatus(savedSearch.status || "");
    setSelectedLocation(savedSearch.location || "");
    setMinMembers(savedSearch.minMembers || "");
    setMaxMembers(savedSearch.maxMembers || "");
    setSortBy(savedSearch.sortBy || "createdAt");
    setSortOrder(savedSearch.sortOrder || "desc");
    setShowSavedSearches(false);
  };

  const deleteSavedSearch = (searchId) => {
    const newSavedSearches = savedSearches.filter((s) => s.id !== searchId);
    setSavedSearches(newSavedSearches);
    localStorage.setItem("savedSearches", JSON.stringify(newSavedSearches));
  };

  const fetchStudies = async () => {
    try {
      setLoading(true);
      const response = await fetch("http://localhost:3001/api/post", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        setStudies(data.data || []);
      } else {
        setError("스터디 목록을 불러오는데 실패했습니다.");
      }
    } catch (error) {
      console.error("Fetch studies error:", error);
      setError("스터디 목록을 불러오는 중 오류가 발생했습니다.");
    } finally {
      setLoading(false);
    }
  };

  const filteredStudies = studies
    .filter((study) => {
      const matchesSearch =
        !debouncedSearchTerm ||
        study.title.toLowerCase().includes(debouncedSearchTerm.toLowerCase()) ||
        study.description
          .toLowerCase()
          .includes(debouncedSearchTerm.toLowerCase()) ||
        (study.author?.username &&
          study.author.username
            .toLowerCase()
            .includes(debouncedSearchTerm.toLowerCase()));

      const matchesCategory =
        !selectedCategory || study.category === selectedCategory;
      const matchesMode = !selectedMode || study.mode === selectedMode;
      const matchesLocation =
        !selectedLocation ||
        (study.location &&
          study.location
            .toLowerCase()
            .includes(selectedLocation.toLowerCase()));

      // Status filtering
      let matchesStatus = true;
      if (selectedStatus === "recruiting") {
        matchesStatus =
          !study.isClosed && study.currentNumber < study.recruitNumber;
      } else if (selectedStatus === "full") {
        matchesStatus = study.currentNumber >= study.recruitNumber;
      } else if (selectedStatus === "closed") {
        matchesStatus = study.isClosed;
      }

      // Member count filtering
      const matchesMinMembers =
        !minMembers || study.recruitNumber >= parseInt(minMembers);
      const matchesMaxMembers =
        !maxMembers || study.recruitNumber <= parseInt(maxMembers);

      return (
        matchesSearch &&
        matchesCategory &&
        matchesMode &&
        matchesStatus &&
        matchesLocation &&
        matchesMinMembers &&
        matchesMaxMembers
      );
    })
    .sort((a, b) => {
      let aValue, bValue;

      switch (sortBy) {
        case "title":
          aValue = a.title.toLowerCase();
          bValue = b.title.toLowerCase();
          break;
        case "category":
          aValue = a.category.toLowerCase();
          bValue = b.category.toLowerCase();
          break;
        case "recruitNumber":
          aValue = a.recruitNumber;
          bValue = b.recruitNumber;
          break;
        case "currentNumber":
          aValue = a.currentNumber || 0;
          bValue = b.currentNumber || 0;
          break;
        case "createdAt":
        default:
          aValue = new Date(a.createdAt);
          bValue = new Date(b.createdAt);
          break;
      }

      if (sortOrder === "asc") {
        return aValue > bValue ? 1 : -1;
      } else {
        return aValue < bValue ? 1 : -1;
      }
    });

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    saveSearchHistory(searchTerm);
    setShowSearchHistory(false);
  };

  const handleSearchHistoryClick = (term) => {
    setSearchTerm(term);
    setShowSearchHistory(false);
  };

  // Highlight search terms in text
  const highlightText = (text, searchTerm) => {
    if (!searchTerm || !text) return text;

    const regex = new RegExp(`(${searchTerm})`, "gi");
    const parts = text.split(regex);

    return parts.map((part, index) =>
      regex.test(part) ? (
        <mark key={index} className="bg-yellow-200 px-1 rounded">
          {part}
        </mark>
      ) : (
        part
      ),
    );
  };

  // Close search history when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (showSearchHistory && !event.target.closest(".search-container")) {
        setShowSearchHistory(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [showSearchHistory]);

  const formatDate = (dateString) => {
    if (!dateString) return "미정";
    return new Date(dateString).toLocaleDateString("ko-KR");
  };

  const getStatusBadge = (study) => {
    if (study.isClosed) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">
          모집 완료
        </span>
      );
    }
    if (study.currentNumber >= study.recruitNumber) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">
          인원 마감
        </span>
      );
    }
    return (
      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
        모집 중
      </span>
    );
  };

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="flex justify-center">
            <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-indigo-600"></div>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="text-center">
            <h2 className="text-2xl font-bold text-gray-900 mb-4">
              오류가 발생했습니다
            </h2>
            <p className="text-gray-600 mb-4">{error}</p>
            <button
              onClick={fetchStudies}
              className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700"
            >
              다시 시도
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900">스터디 목록</h1>
          <button
            onClick={() => navigate("/create-study")}
            className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700"
          >
            새 스터디 만들기
          </button>
        </div>

        {/* 검색 및 필터 */}
        <div className="bg-white p-6 rounded-lg shadow mb-6">
          {/* 기본 검색 및 필터 */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-4">
            <div className="relative search-container">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                검색
              </label>
              <form onSubmit={handleSearchSubmit} className="relative">
                <input
                  type="text"
                  placeholder="스터디 제목, 내용, 작성자로 검색"
                  value={searchTerm}
                  onChange={(e) => {
                    setSearchTerm(e.target.value);
                    setShowSearchHistory(true);
                  }}
                  onFocus={() => setShowSearchHistory(true)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                />
                <button
                  type="submit"
                  className="absolute right-2 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
                >
                  <svg
                    className="w-5 h-5"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
                    />
                  </svg>
                </button>
              </form>

              {/* 검색 기록 드롭다운 */}
              {showSearchHistory && searchHistory.length > 0 && (
                <div className="absolute z-10 w-full mt-1 bg-white border border-gray-300 rounded-md shadow-lg">
                  <div className="p-2 border-b border-gray-200 flex justify-between items-center">
                    <span className="text-sm font-medium text-gray-700">
                      최근 검색어
                    </span>
                    <button
                      onClick={clearSearchHistory}
                      className="text-xs text-red-600 hover:text-red-800"
                    >
                      전체 삭제
                    </button>
                  </div>
                  {searchHistory.map((term, index) => (
                    <button
                      key={index}
                      onClick={() => handleSearchHistoryClick(term)}
                      className="w-full text-left px-3 py-2 text-sm text-gray-700 hover:bg-gray-100 flex items-center"
                    >
                      <svg
                        className="w-4 h-4 mr-2 text-gray-400"
                        fill="none"
                        stroke="currentColor"
                        viewBox="0 0 24 24"
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          strokeWidth={2}
                          d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z"
                        />
                      </svg>
                      {term}
                    </button>
                  ))}
                </div>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                분류
              </label>
              <select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
              >
                <option value="">전체</option>
                <option value="프로그래밍">프로그래밍</option>
                <option value="언어">언어</option>
                <option value="자격증">자격증</option>
                <option value="취업준비">취업준비</option>
                <option value="기타">기타</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                모드
              </label>
              <select
                value={selectedMode}
                onChange={(e) => setSelectedMode(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
              >
                <option value="">전체</option>
                <option value="온라인">온라인</option>
                <option value="오프라인">오프라인</option>
                <option value="하이브리드">하이브리드</option>
              </select>
            </div>

            <div className="grid grid-cols-3 gap-2">
              <button
                onClick={() => setShowAdvancedFilters(!showAdvancedFilters)}
                className="w-full bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 text-sm whitespace-nowrap"
              >
                {showAdvancedFilters ? "고급 필터 숨기기" : "고급 필터"}
              </button>
              <button
                onClick={() => setShowSavedSearches(!showSavedSearches)}
                className="w-full bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700 text-sm whitespace-nowrap"
              >
                검색
              </button>
              <button
                onClick={() => {
                  setSearchTerm("");
                  setSelectedCategory("");
                  setSelectedMode("");
                  setSelectedStatus("");
                  setSelectedLocation("");
                  setMinMembers("");
                  setMaxMembers("");
                }}
                className="w-full bg-gray-600 text-white px-4 py-2 rounded-md hover:bg-gray-700 text-sm whitespace-nowrap"
              >
                초기화
              </button>
            </div>
          </div>

          {/* 저장된 검색 드롭다운 */}
          {showSavedSearches && (
            <div className="border-t border-gray-200 pt-4">
              <div className="flex items-center justify-between mb-3">
                <h4 className="text-sm font-medium text-gray-700">
                  저장된 검색 조건
                </h4>
                <button
                  onClick={saveCurrentSearch}
                  className="text-sm text-indigo-600 hover:text-indigo-800 underline"
                >
                  현재 검색 저장
                </button>
              </div>
              {savedSearches.length === 0 ? (
                <p className="text-sm text-gray-500 text-center py-4">
                  저장된 검색 조건이 없습니다.
                </p>
              ) : (
                <div className="space-y-2">
                  {savedSearches.map((savedSearch) => (
                    <div
                      key={savedSearch.id}
                      className="flex items-center justify-between p-3 bg-gray-50 rounded-lg"
                    >
                      <div className="flex-1">
                        <div className="flex items-center space-x-2">
                          <button
                            onClick={() => loadSavedSearch(savedSearch)}
                            className="text-sm font-medium text-gray-900 hover:text-indigo-600"
                          >
                            {savedSearch.name}
                          </button>
                          <span className="text-xs text-gray-500">
                            {new Date(
                              savedSearch.createdAt,
                            ).toLocaleDateString()}
                          </span>
                        </div>
                        <div className="text-xs text-gray-600 mt-1">
                          {savedSearch.searchTerm &&
                            `검색어: ${savedSearch.searchTerm}`}
                          {savedSearch.category &&
                            ` | 분류: ${savedSearch.category}`}
                          {savedSearch.mode && ` | 모드: ${savedSearch.mode}`}
                        </div>
                      </div>
                      <button
                        onClick={() => deleteSavedSearch(savedSearch.id)}
                        className="text-red-600 hover:text-red-800 text-sm"
                        title="삭제"
                      >
                        <svg
                          className="w-4 h-4"
                          fill="none"
                          stroke="currentColor"
                          viewBox="0 0 24 24"
                        >
                          <path
                            strokeLinecap="round"
                            strokeLinejoin="round"
                            strokeWidth={2}
                            d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16"
                          />
                        </svg>
                      </button>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}

          {/* 고급 필터 */}
          {showAdvancedFilters && (
            <div className="border-t border-gray-200 pt-4">
              <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    상태
                  </label>
                  <select
                    value={selectedStatus}
                    onChange={(e) => setSelectedStatus(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                  >
                    <option value="">전체</option>
                    <option value="recruiting">모집 중</option>
                    <option value="full">인원 마감</option>
                    <option value="closed">모집 완료</option>
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    위치
                  </label>
                  <input
                    type="text"
                    placeholder="위치로 검색"
                    value={selectedLocation}
                    onChange={(e) => setSelectedLocation(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    최소 인원
                  </label>
                  <input
                    type="number"
                    placeholder="최소"
                    value={minMembers}
                    onChange={(e) => setMinMembers(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    최대 인원
                  </label>
                  <input
                    type="number"
                    placeholder="최대"
                    value={maxMembers}
                    onChange={(e) => setMaxMembers(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                  />
                </div>

                <div className="flex items-end">
                  <div className="w-full text-center">
                    <p className="text-sm text-gray-600 mb-1">검색 결과</p>
                    <p className="text-lg font-semibold text-indigo-600">
                      {filteredStudies.length}개
                    </p>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* 검색 결과 요약 및 정렬 */}
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between mb-6">
          {/* 검색 결과 요약 */}
          {debouncedSearchTerm ||
          selectedCategory ||
          selectedMode ||
          selectedStatus ||
          selectedLocation ||
          minMembers ||
          maxMembers ? (
            <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-4 sm:mb-0 flex-1">
              <div className="flex items-center justify-between">
                <div>
                  <h3 className="text-sm font-medium text-blue-900">
                    검색 결과
                  </h3>
                  <p className="text-sm text-blue-700 mt-1">
                    총{" "}
                    <span className="font-semibold">
                      {filteredStudies.length}개
                    </span>
                    의 스터디를 찾았습니다
                    {debouncedSearchTerm && (
                      <span className="ml-2">
                        (검색어:{" "}
                        <span className="font-semibold">
                          "{debouncedSearchTerm}"
                        </span>
                        )
                      </span>
                    )}
                  </p>
                </div>
                <button
                  onClick={() => {
                    setSearchTerm("");
                    setSelectedCategory("");
                    setSelectedMode("");
                    setSelectedStatus("");
                    setSelectedLocation("");
                    setMinMembers("");
                    setMaxMembers("");
                  }}
                  className="text-sm text-blue-600 hover:text-blue-800 underline"
                >
                  검색 조건 초기화
                </button>
              </div>
            </div>
          ) : (
            <div className="mb-4 sm:mb-0">
              <p className="text-sm text-gray-600">
                총{" "}
                <span className="font-semibold">
                  {filteredStudies.length}개
                </span>
                의 스터디
              </p>
            </div>
          )}

          {/* 정렬 옵션 */}
          {filteredStudies.length > 0 && (
            <div className="flex items-center space-x-2">
              <label className="text-sm font-medium text-gray-700">정렬:</label>
              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                className="px-3 py-1 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
              >
                <option value="createdAt">최신순</option>
                <option value="title">제목순</option>
                <option value="category">분류순</option>
                <option value="recruitNumber">모집인원순</option>
                <option value="currentNumber">현재인원순</option>
              </select>
              <button
                onClick={() =>
                  setSortOrder(sortOrder === "asc" ? "desc" : "asc")
                }
                className="p-1 text-gray-400 hover:text-gray-600"
                title={sortOrder === "asc" ? "내림차순" : "오름차순"}
              >
                {sortOrder === "asc" ? (
                  <svg
                    className="w-4 h-4"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M5 15l7-7 7 7"
                    />
                  </svg>
                ) : (
                  <svg
                    className="w-4 h-4"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M19 9l-7 7-7-7"
                    />
                  </svg>
                )}
              </button>
            </div>
          )}
        </div>

        {/* 스터디 목록 */}
        {filteredStudies.length === 0 ? (
          <div className="text-center py-12">
            <div className="mb-4">
              <svg
                className="mx-auto h-12 w-12 text-gray-400"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"
                />
              </svg>
            </div>
            <h3 className="text-lg font-medium text-gray-900 mb-2">
              {studies.length === 0
                ? "스터디가 없습니다"
                : "검색 결과가 없습니다"}
            </h3>
            <p className="text-gray-600 mb-4">
              {studies.length === 0
                ? "첫 번째 스터디를 만들어보세요!"
                : "다른 검색 조건을 시도해보세요."}
            </p>
            {studies.length === 0 ? (
              <button
                onClick={() => navigate("/create-study")}
                className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700"
              >
                스터디 만들기
              </button>
            ) : (
              <button
                onClick={() => {
                  setSearchTerm("");
                  setSelectedCategory("");
                  setSelectedMode("");
                  setSelectedStatus("");
                  setSelectedLocation("");
                  setMinMembers("");
                  setMaxMembers("");
                }}
                className="bg-gray-600 text-white px-4 py-2 rounded-md hover:bg-gray-700"
              >
                검색 조건 초기화
              </button>
            )}
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredStudies.map((study) => (
              <div
                key={study.id}
                className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow"
              >
                <div className="p-6">
                  <div className="flex justify-between items-start mb-4">
                    <h3 className="text-xl font-semibold text-gray-900 line-clamp-2">
                      {highlightText(study.title, debouncedSearchTerm)}
                    </h3>
                    {getStatusBadge(study)}
                  </div>

                  <p className="text-gray-600 text-sm mb-4 line-clamp-3">
                    {highlightText(study.description, debouncedSearchTerm)}
