  const [error, setError] = useState(null);

  const fetchMyStudies = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem("token");

      const response = await fetch(
        "http://localhost:3001/api/post/user/my-studies",
        {
          headers: { Authorization: `Bearer ${token}` },
        },
      );

      if (response.ok) {
        const data = await response.json();
        setStudies(data);
      } else {
        setError("스터디 목록을 불러오는데 실패했습니다.");
      }
    } catch (error) {
      console.error("Failed to fetch my studies:", error);
      setError("스터디 목록을 불러오는 중 오류가 발생했습니다.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (user) {
      fetchMyStudies();
    }
  }, [user]);

  // user가 null인 경우 로딩 상태 표시
  if (!user) {
