const StudyDetail = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const { id } = useParams();
  const [study, setStudy] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [sessions, setSessions] = useState([]);
  const [members, setMembers] = useState([]);
  const [activeTab, setActiveTab] = useState("info");
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState("");
  const [commentLoading, setCommentLoading] = useState(false);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [myApplications, setMyApplications] = useState([]);
  const [showApplicationForm, setShowApplicationForm] = useState(false);
  const [applicationAnswers, setApplicationAnswers] = useState({});


  console.log("=== StudyDetail Component ===");
  console.log("URL ID from useParams:", id);
  console.log("URL ID type:", typeof id);
  console.log("Current URL:", window.location.href);

  useEffect(() => {
    if (id) {
      fetchStudyDetail();
      fetchStudySessions();
      fetchStudyMembers();
      fetchComments();
      if (user) {
        fetchMyApplications();
      }
    }
  }, [id, user]);

  const fetchStudyDetail = async () => {
    try {
      setLoading(true);
      console.log("Fetching study detail for ID:", id);
      console.log("Fetching study detail for ID type:", typeof id);
      console.log("API URL:", `http://localhost:3001/api/post/${id}/detail`);
      const token = localStorage.getItem("token");
      console.log("Token for study detail:", token);

      const response = await fetch(
        `http://localhost:3001/api/post/${id}/detail`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        },
      );

      console.log("Study detail response status:", response.status);

      if (response.ok) {
        const data = await response.json();
        console.log("Study detail data:", data);
        console.log("Study ID from response:", data.data?.id || data.id);
        console.log(
          "Study title from response:",
          data.data?.title || data.title,
        );
        console.log(
          "Study applicationFormTemplate:",
          data.data?.applicationFormTemplate || data.applicationFormTemplate,
        );
        console.log(
          "Study applicationFormTemplate length:",
          (data.data?.applicationFormTemplate || data.applicationFormTemplate)
            ?.length,
        );
        setStudy(data.data || data);
      } else {
        const errorData = await response.json();
        console.error("Study detail error response:", errorData);
        setError("스터디 정보를 불러오는데 실패했습니다.");
      }
    } catch (error) {
      console.error("Fetch study detail error:", error);
      setError("스터디 정보를 불러오는 중 오류가 발생했습니다.");
    } finally {
      setLoading(false);
    }
  };

  const fetchStudySessions = async () => {
    try {
      const response = await fetch(
        `http://localhost:3001/api/post/${id}/sessions`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        },
      );

      if (response.ok) {
        const data = await response.json();
        setSessions(data.data || data || []);
      }
    } catch (error) {
      console.error("Fetch sessions error:", error);
    }
  };

  const fetchStudyMembers = async () => {
    try {
      const response = await fetch(`http://localhost:3001/api/post/${id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        setMembers(data.data?.studymemberships || data.studymemberships || []);
      }
    } catch (error) {
      console.error("Fetch members error:", error);
    }
  };

  const fetchComments = async () => {
    try {
      const response = await fetch(`http://localhost:3001/api/comment?postId=${id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        console.log("댓글 데이터:", data);
        console.log("댓글 배열:", data.data || data || []);
        setComments(data.data || data || []);
      }
    } catch (error) {
      console.error("Fetch comments error:", error);
    }
  };

  const fetchMyApplications = async () => {
    try {
      const response = await fetch(
        `http://localhost:3001/api/post/my-applications`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        },
      );

      if (response.ok) {
        const data = await response.json();
        setMyApplications(data.data || data || []);
      }
    } catch (error) {
      console.error("Fetch my applications error:", error);
    }
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    if (!newComment.trim() && selectedFiles.length === 0) return;

    setCommentLoading(true);
    try {
      // FormData를 사용하여 댓글과 파일을 함께 전송
      const formData = new FormData();
      formData.append('content', newComment);
      formData.append('postId', id.toString());

      // 파일들을 추가
      selectedFiles.forEach(file => {
        formData.append('files', file);
      });

      const response = await fetch("http://localhost:3001/api/comment", {
        method: "POST",
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: formData,
      });

      if (response.ok) {
        setNewComment("");
        setSelectedFiles([]);
        fetchComments(); // 댓글 목록 새로고침
      } else {
        console.error("Failed to create comment");
      }
    } catch (error) {
      console.error("Create comment error:", error);
    } finally {
      setCommentLoading(false);
    }
  };

  const handleDeleteComment = async (commentId) => {
    try {
      const response = await fetch(`http://localhost:3001/api/comment/${commentId}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });

      if (response.ok) {
        fetchComments(); // 댓글 목록 새로고침
      }
    } catch (error) {
      console.error("Delete comment error:", error);
    }
  };

  const handleFileSelect = (e) => {
    const files = Array.from(e.target.files);

    // 파일 개수 제한 (최대 5개)
    if (selectedFiles.length + files.length > 5) {
      alert('최대 5개까지만 첨부할 수 있습니다.');
      return;
    }

    // 파일 크기 제한 (각 10MB 이하)
    const oversizedFiles = files.filter(file => file.size > 10 * 1024 * 1024);
    if (oversizedFiles.length > 0) {
      alert('10MB 이하의 파일만 첨부할 수 있습니다.');
      return;
    }

    setSelectedFiles(prev => [...prev, ...files]);
  };

  const removeFile = (index) => {
    setSelectedFiles(prev => prev.filter((_, i) => i !== index));
  };





  const handleLeaveStudy = async () => {
    try {
      const response = await fetch(
        `http://localhost:3001/api/post/${id}/leave`,
        {
          method: "DELETE",
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
