            </div>
          </div>

          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="flex justify-between items-start mb-4">
              <h1 className="text-3xl font-bold text-gray-900">
                {study?.title || "제목 없음"}
              </h1>
              {getStatusBadge(study)}
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
              <div className="flex items-center text-sm text-gray-500">
                <span className="font-medium mr-2">분류:</span>
                <span className="bg-blue-100 text-blue-800 px-2 py-1 rounded text-xs">
                  {study.category}
                </span>
              </div>
              <div className="flex items-center text-sm text-gray-500">
                <span className="font-medium mr-2">모드:</span>
                <span className="bg-green-100 text-green-800 px-2 py-1 rounded text-xs">
                  {study.mode}
                </span>
              </div>
              {study.location && (
                <div className="flex items-center text-sm text-gray-500">
                  <span className="font-medium mr-2">위치:</span>
                  <span>{study.location}</span>
                </div>
              )}
              <div className="flex items-center text-sm text-gray-500">
                <span className="font-medium mr-2">인원:</span>
                <span>
                  {study.currentNumber || 1}/{study.recruitNumber}명
                </span>
              </div>
              <div className="flex items-center text-sm text-gray-500">
                <span className="font-medium mr-2">작성자:</span>
                <span>{study.author?.username || "알 수 없음"}</span>
              </div>
              <div className="flex items-center text-sm text-gray-500">
                <span className="font-medium mr-2">작성일:</span>
                <span>{formatDate(study.createdAt)}</span>
              </div>
            </div>
          </div>
        </div>

        {/* 탭 네비게이션 */}
        <div className="bg-white rounded-lg shadow-md mb-6">
          <div className="border-b border-gray-200">
            <nav className="-mb-px flex space-x-8 px-6">
              <button
                onClick={() => setActiveTab("info")}
                className={`py-4 px-1 border-b-2 font-medium text-sm ${
                  activeTab === "info"
                    ? "border-indigo-500 text-indigo-600"
                    : "border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300"
                }`}
              >
                스터디 정보
              </button>
              {study?.studyStatus === 'accepted' && (
                <button
                  onClick={() => setActiveTab("sessions")}
                  className={`py-4 px-1 border-b-2 font-medium text-sm ${
                    activeTab === "sessions"
                      ? "border-indigo-500 text-indigo-600"
                      : "border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300"
                  }`}
                >
                  스터디 세션 ({sessions.length})
                </button>
              )}
              {study?.studyStatus === 'accepted' && (
                <button
                  onClick={() => setActiveTab("members")}
                  className={`py-4 px-1 border-b-2 font-medium text-sm ${
                    activeTab === "members"
                      ? "border-indigo-500 text-indigo-600"
                      : "border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300"
                  }`}
                >
                  멤버 ({members.length})
                </button>
              )}
              <button
                onClick={() => setActiveTab("comments")}
                className={`py-4 px-1 border-b-2 font-medium text-sm ${
                  activeTab === "comments"
                    ? "border-indigo-500 text-indigo-600"
                    : "border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300"
                }`}
              >
                댓글 ({comments.length})
              </button>
            </nav>
          </div>

          <div className="p-6">
            {/* 스터디 정보 탭 */}
            {activeTab === "info" && (
              <div>
                <h3 className="text-lg font-medium text-gray-900 mb-4">
                  스터디 설명
                </h3>
                <div className="prose max-w-none">
                  <p className="text-gray-700 whitespace-pre-wrap">
                    {study.description}
                  </p>
                </div>

                {(study.studyStartDate || study.studyEndDate) && (
                  <div className="mt-6">
                    <h4 className="text-md font-medium text-gray-900 mb-2">
                      스터디 기간
                    </h4>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      {study.studyStartDate && (
                        <div>
                          <span className="text-sm font-medium text-gray-500">
                            시작일:
                          </span>
                          <span className="ml-2 text-sm text-gray-900">
                            {formatDate(study.studyStartDate)}
                          </span>
                        </div>
                      )}
                      {study.studyEndDate && (
                        <div>
                          <span className="text-sm font-medium text-gray-500">
                            종료일:
                          </span>
                          <span className="ml-2 text-sm text-gray-900">
                            {formatDate(study.studyEndDate)}
                          </span>
                        </div>
                      )}
                    </div>
                  </div>
                )}
              </div>
            )}

            {/* 스터디 세션 탭 */}
            {activeTab === "sessions" && (
              <div>
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-lg font-medium text-gray-900">
                    스터디 세션
                  </h3>
                  {isAuthor && (
                    <button
                      onClick={() => navigate(`/studies/${id}/sessions/create`)}
                      className="bg-indigo-600 text-white px-3 py-1 rounded-md hover:bg-indigo-700 text-sm"
                    >
                      세션 추가
                    </button>
                  )}
                </div>

                {sessions.length === 0 ? (
                  <p className="text-gray-500">
                    등록된 스터디 세션이 없습니다.
                  </p>
                ) : (
                  <div className="space-y-4">
                    {sessions.map((session) => (
                      <div
                        key={session.id}
                        className="border border-gray-200 rounded-lg p-4"
                      >
                        <div className="flex justify-between items-start">
                          <div>
                            <h4 className="font-medium text-gray-900">
                              {session.title || `${session.sessionNumber}회차`}
                            </h4>
                            {session.description && (
                              <p className="text-sm text-gray-600 mt-1">
                                {session.description}
                              </p>
                            )}
                            <div className="mt-2 text-sm text-gray-500">
                              <span>
                                일시: {formatDateTime(session.scheduledDate)}
                              </span>
                            </div>
                          </div>
                          <span
                            className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                              session.status === "SCHEDULED"
                                ? "bg-blue-100 text-blue-800"
                                : session.status === "IN_PROGRESS"
                                  ? "bg-green-100 text-green-800"
                                  : "bg-gray-100 text-gray-800"
                            }`}
                          >
                            {session.status === "SCHEDULED"
                              ? "예정"
                              : session.status === "IN_PROGRESS"
                                ? "진행중"
                                : "완료"}
                          </span>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}

            {/* 멤버 탭 */}
            {activeTab === "members" && (
              <div>
                <h3 className="text-lg font-medium text-gray-900 mb-4">
                  스터디 멤버
                </h3>

                {members.length === 0 ? (
                  <p className="text-gray-500">등록된 멤버가 없습니다.</p>
                ) : (
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {members.map((member) => (
                      <div
                        key={member.id}
                        className="flex items-center space-x-3 p-3 border border-gray-200 rounded-lg"
                      >
                        <div className="h-10 w-10 bg-indigo-600 rounded-full flex items-center justify-center">
                          <span className="text-white text-sm font-medium">
                            {member.user?.username?.charAt(0).toUpperCase()}
                          </span>
                        </div>
                        <div>
                          <p className="text-sm font-medium text-gray-900">
                            {member.user?.username}
                          </p>
                          <p className="text-xs text-gray-500">
                            {member.user?.email}
                          </p>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}

            {/* 댓글 탭 */}
            {activeTab === "comments" && (
              <div>
                <h3 className="text-lg font-medium text-gray-900 mb-4">
                  댓글
                </h3>

                {/* 댓글 작성 폼 */}
                {user && (
                  <div className="mb-6 p-4 border border-gray-200 rounded-lg bg-gray-50">
                    <form onSubmit={handleCommentSubmit} className="space-y-3">
                      <textarea
                        value={newComment}
                        onChange={(e) => setNewComment(e.target.value)}
                        placeholder="댓글을 작성해주세요..."
                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 resize-none"
                        rows={3}
                      />

                      {/* 파일 업로드 섹션 */}
                      <div className="space-y-3">
                        <div className="flex items-center space-x-2">
                          <label className="flex items-center space-x-2 cursor-pointer bg-white px-3 py-2 border border-gray-300 rounded-md hover:bg-gray-50">
                            <svg className="w-5 h-5 text-gray-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
                            </svg>
                            <span className="text-sm text-gray-600">파일 첨부</span>
                            <input
                              type="file"
                              multiple
                              onChange={handleFileSelect}
                              className="hidden"
                              accept="image/*,.pdf,.doc,.docx,.txt,.zip,.rar"
                            />
                          </label>
                          <span className="text-xs text-gray-500">
                            최대 5개 파일, 각 10MB 이하
                          </span>
                        </div>

                        {/* 선택된 파일 목록 */}
                        {selectedFiles.length > 0 && (
                          <div className="space-y-2">
                            <p className="text-sm font-medium text-gray-700">선택된 파일:</p>
                            {selectedFiles.map((file, index) => (
                              <div key={index} className="flex items-center justify-between bg-white p-2 border border-gray-200 rounded-md">
                                <div className="flex items-center space-x-2">
                                  <svg className="w-4 h-4 text-gray-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15.172 7l-6.586 6.586a2 2 0 102.828 2.828l6.414-6.586a4 4 0 00-5.656-5.656l-6.415 6.585a6 6 0 108.486 8.486L20.5 13" />
                                  </svg>
                                  <span className="text-sm text-gray-700">{file.name}</span>
                                  <span className="text-xs text-gray-500">
                                    ({(file.size / 1024 / 1024).toFixed(2)} MB)
                                  </span>
                                </div>
                                <button
                                  type="button"
                                  onClick={() => removeFile(index)}
                                  className="text-red-500 hover:text-red-700 text-sm"
                                >
                                  삭제
                                </button>
                              </div>
                            ))}
                          </div>
                        )}
                      </div>

                      <div className="flex justify-end">
                        <button
                          type="submit"
                          disabled={commentLoading || (!newComment.trim() && selectedFiles.length === 0)}
                          className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700 disabled:bg-gray-400 disabled:cursor-not-allowed text-sm"
                        >
                                                      {commentLoading ? "업로드 중..." : "댓글 작성"}
                        </button>
                      </div>
                    </form>
                  </div>
                )}

                {/* 댓글 목록 */}
                {comments.length === 0 ? (
                  <p className="text-gray-500">아직 댓글이 없습니다.</p>
                ) : (
                  <div className="space-y-4">
                    {comments.map((comment) => {
                      console.log("개별 댓글:", comment);
                      console.log("댓글 내용:", comment.content);
                      console.log("댓글 파일:", comment.files);
                      return (
                        <div
                          key={comment.id}
                          className="p-4 border border-gray-200 rounded-lg bg-white"
                        >
                          <div className="flex items-start justify-between">
                            <div className="flex items-center space-x-3 mb-2">
                              <div className="h-8 w-8 bg-indigo-600 rounded-full flex items-center justify-center">
                                <span className="text-white text-xs font-medium">
                                  {comment.author?.username?.charAt(0).toUpperCase()}
                                </span>
                              </div>
                              <div>
                                <p className="text-sm font-medium text-gray-900">
                                  {comment.author?.username}
                                </p>
                                <p className="text-xs text-gray-500">
                                  {formatDate(comment.createdAt)}
                                </p>
                              </div>
                            </div>
                            {user && comment.author?.id === user.id && (
                              <button
                                onClick={() => handleDeleteComment(comment.id)}
                                className="text-red-500 hover:text-red-700 text-sm"
                              >
                                삭제
                              </button>
                            )}
                          </div>
                          <div className="ml-11">
                            <p className="text-gray-700 whitespace-pre-wrap">
                              {comment.content}
                            </p>

                            {/* 첨부된 파일들 표시 */}
                            {comment.files && comment.files.length > 0 && (
                              <div className="mt-3 space-y-2">
                                <p className="text-sm font-medium text-gray-600">첨부 파일:</p>
                                <div className="space-y-2">
                                  {comment.files.map((file, fileIndex) => (
                                    <div key={fileIndex} className="flex items-center space-x-2 bg-gray-50 p-2 rounded-md">
                                      <svg className="w-4 h-4 text-gray-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15.172 7l-6.586 6.586a2 2 0 102.828 2.828l6.414-6.586a4 4 0 00-5.656-5.656l-6.415 6.585a6 6 0 108.486 8.486L20.5 13" />
                                      </svg>
                                      <a
                                        href={file.fileUrl}
                                        target="_blank"
                                        rel="noopener noreferrer"
                                        className="text-sm text-indigo-600 hover:text-indigo-800 underline"
                                      >
                                        {file.originalName}
                                      </a>
                                      <span className="text-xs text-gray-500">
                                        ({(file.fileSize / 1024 / 1024).toFixed(2)} MB)
                                      </span>
                                    </div>
                                  ))}
                                </div>
                              </div>
                            )}
                          </div>
                        </div>
                      );
                    })}
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

const ProtectedRoute = ({ children }) => {
  const { user, loading } = useAuth();

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  if (!user) {
    return <Navigate to="/login" replace />;
  }

  return <>{children}</>;
};

const Layout = ({ children }) => {
  const { user } = useAuth();
  return (
    <div className="min-h-screen bg-gray-50">
      {user && <Header />}
      <main>{children}</main>
    </div>
  );
};

const App = () => {
  return (
    <QueryClientProvider client={queryClient}>
      <Router>
        <AuthProvider>
          <Layout>
            <Routes>
              <Route
                path="/"
                element={
                  <ProtectedRoute>
                    <Home />
                  </ProtectedRoute>
                }
              />
              <Route path="/login" element={<LoginForm />} />
              <Route path="/register" element={<RegisterForm />} />
              <Route
                path="/me"
                element={
                  <ProtectedRoute>
                    <UserProfile />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/studies"
                element={
                  <ProtectedRoute>
                    <StudyList />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/studies/:id"
                element={
                  <ProtectedRoute>
                    <StudyDetail />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/studies/:id/applicants"
                element={
                  <ProtectedRoute>
                    <StudyApplicants />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/my-studies"
                element={
                  <ProtectedRoute>
                    <MyStudies />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/my-applications"
                element={
                  <ProtectedRoute>
                    <MyApplications />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/my-memberships"
                element={
                  <ProtectedRoute>
                    <MyMemberships />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/my-member-studies"
                element={
                  <ProtectedRoute>
                    <MyMemberStudies />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/my-leader-studies"
                element={
                  <ProtectedRoute>
                    <MyLeaderStudies />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/create-study"
                element={
                  <ProtectedRoute>
                    <CreateStudy />
                  </ProtectedRoute>
                }
              />
              <Route path="*" element={<Navigate to="/" replace />} />
            </Routes>
          </Layout>
          <Toaster position="top-right" />
        </AuthProvider>
      </Router>
    </QueryClientProvider>
  );
};

export default App;
