const CreateStudy = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    category: "",
    mode: "",
    location: "",
    recruitNumber: 1,
    studyStartDate: "",
    studyEndDate: "",
  });
  const [errors, setErrors] = useState({});

  // Request form state
  const [showRequestForm, setShowRequestForm] = useState(false);
  const [requestFormData, setRequestFormData] = useState({
    title: "스터디 지원 양식",
    questions: [],
  });
  const [requestFormErrors, setRequestFormErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};
    if (!formData.title.trim()) newErrors.title = "스터디 제목을 입력해주세요.";
    if (!formData.description.trim())
      newErrors.description = "스터디 내용을 입력해주세요.";
    if (!formData.category) newErrors.category = "스터디 분류를 선택해주세요.";
    if (!formData.mode) newErrors.mode = "스터디 모드를 선택해주세요.";
    if (formData.recruitNumber < 1)
      newErrors.recruitNumber = "모집 인원은 1명 이상이어야 합니다.";
    if (
      formData.studyStartDate &&
      formData.studyEndDate &&
      new Date(formData.studyStartDate) > new Date(formData.studyEndDate)
    ) {
      newErrors.studyEndDate = "종료일은 시작일보다 늦어야 합니다.";
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validateRequestForm = () => {
    const newErrors = {};
    if (!requestFormData.title.trim())
      newErrors.title = "양식 제목을 입력해주세요.";
    if (requestFormData.questions.length === 0)
      newErrors.questions = "최소 하나의 질문을 추가해주세요.";

    // Validate each question
    requestFormData.questions.forEach((question, index) => {
      if (!question.questionText.trim()) {
        newErrors[`question_${index}`] = "질문 내용을 입력해주세요.";
      }

      // 체크박스나 객관식 선택형인 경우 옵션이 필요
      if (
        (question.type === "checkbox" || question.type === "multiple_choice") &&
        (!question.options || question.options.length === 0)
      ) {
        newErrors[`question_${index}_options`] =
          `${question.type === "checkbox" ? "체크박스" : "객관식"} 옵션을 최소 하나 추가해주세요.`;
      }

      // 옵션이 있는 경우 빈 옵션이 있는지 확인
      if (question.options && question.options.length > 0) {
        const emptyOptions = question.options.filter(
          (option) => !option.trim(),
        );
        if (emptyOptions.length > 0) {
          newErrors[`question_${index}_options`] =
            "빈 옵션이 있습니다. 모든 옵션을 입력해주세요.";
        }
      }
    });

    setRequestFormErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    // Validate request form if it's enabled
    if (showRequestForm && !validateRequestForm()) return;

    setLoading(true);
    try {
      // First, create the study
      const studyResponse = await fetch("http://localhost:3001/api/post", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({
          ...formData,
          recruitNumber: parseInt(formData.recruitNumber),
          studyStartDate: formData.studyStartDate || null,
          studyEndDate: formData.studyEndDate || null,
        }),
      });

      const studyData = await studyResponse.json();

      if (studyResponse.ok) {
        // If request form is enabled, create it
        if (showRequestForm && requestFormData.questions.length > 0) {
          // Post가 완전히 저장될 때까지 잠시 대기
          await new Promise((resolve) => setTimeout(resolve, 1000));

          try {
            const formResponse = await fetch(
              "http://localhost:3001/api/requestform",
              {
                method: "POST",
                headers: {
                  "Content-Type": "application/json",
                  Authorization: `Bearer ${localStorage.getItem("token")}`,
                },
                body: JSON.stringify({
                  postId: studyData.data.id,
                  title: requestFormData.title,
                  questions: requestFormData.questions.map((q, index) => ({
                    questionText: q.questionText,
                    type: q.type,
                    isRequired: q.isRequired,
                    order: index + 1,
                    options: q.options || [],
                  })),
                }),
              },
            );

            if (formResponse.ok) {
              toast.success("스터디와 지원 양식이 성공적으로 생성되었습니다!");
            } else {
              toast.success(
                "스터디가 생성되었지만 지원 양식 생성에 실패했습니다.",
              );
            }
          } catch (formError) {
            console.error("Create request form error:", formError);
            toast.success(
              "스터디가 생성되었지만 지원 양식 생성에 실패했습니다.",
            );
          }
        } else {
          toast.success("스터디가 성공적으로 생성되었습니다!");
        }
        navigate("/studies");
      } else {
        toast.error(studyData.message || "스터디 생성에 실패했습니다.");
      }
    } catch (error) {
      console.error("Create study error:", error);
      toast.error("스터디 생성 중 오류가 발생했습니다.");
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    if (errors[name]) setErrors({ ...errors, [name]: "" });
  };

  // Request form helper functions
  const addQuestion = () => {
    setRequestFormData({
      ...requestFormData,
      questions: [
        ...requestFormData.questions,
        {
          questionText: "",
          type: "short_answer",
          isRequired: true,
          options: [],
        },
      ],
    });
  };

  const removeQuestion = (index) => {
    const newQuestions = requestFormData.questions.filter(
      (_, i) => i !== index,
    );
    setRequestFormData({
      ...requestFormData,
      questions: newQuestions,
    });
  };

  const updateQuestion = (index, field, value) => {
    const newQuestions = [...requestFormData.questions];
    newQuestions[index] = { ...newQuestions[index], [field]: value };
    setRequestFormData({
      ...requestFormData,
      questions: newQuestions,
    });
  };

  const handleRequestFormChange = (e) => {
    const { name, value } = e.target;
    setRequestFormData({ ...requestFormData, [name]: value });
    if (requestFormErrors[name])
      setRequestFormErrors({ ...requestFormErrors, [name]: "" });
  };

  const addOption = (questionIndex) => {
    const newQuestions = [...requestFormData.questions];
    if (!newQuestions[questionIndex].options) {
      newQuestions[questionIndex].options = [];
    }
    newQuestions[questionIndex].options.push("");
    setRequestFormData({
      ...requestFormData,
      questions: newQuestions,
    });
  };

  const removeOption = (questionIndex, optionIndex) => {
    const newQuestions = [...requestFormData.questions];
    newQuestions[questionIndex].options.splice(optionIndex, 1);
    setRequestFormData({
      ...requestFormData,
      questions: newQuestions,
    });
  };

  const updateOption = (questionIndex, optionIndex, value) => {
    const newQuestions = [...requestFormData.questions];
    newQuestions[questionIndex].options[optionIndex] = value;
    setRequestFormData({
      ...requestFormData,
      questions: newQuestions,
    });
  };

  return (
    <div className="max-w-4xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        <div className="bg-white shadow rounded-lg">
          <div className="px-4 py-5 sm:p-6">
            <h1 className="text-3xl font-bold text-gray-900 mb-8">
              새 스터디 만들기
            </h1>

            <form onSubmit={handleSubmit} className="space-y-6">
              <div className="grid grid-cols-1 gap-6 sm:grid-cols-2">
                <div className="sm:col-span-2">
                  <label
                    htmlFor="title"
                    className="block text-sm font-medium text-gray-700"
                  >
                    스터디 제목 *
                  </label>
                  <input
                    type="text"
                    id="title"
                    name="title"
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.title ? "border-red-500" : "border-gray-300"
                    }`}
                    value={formData.title}
                    onChange={handleChange}
                    placeholder="스터디 제목을 입력하세요"
                  />
                  {errors.title && (
                    <p className="mt-1 text-sm text-red-600">{errors.title}</p>
                  )}
                </div>

                <div className="sm:col-span-2">
                  <label
                    htmlFor="description"
                    className="block text-sm font-medium text-gray-700"
                  >
                    스터디 내용 *
                  </label>
                  <textarea
                    id="description"
                    name="description"
                    rows={4}
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.description ? "border-red-500" : "border-gray-300"
                    }`}
                    value={formData.description}
                    onChange={handleChange}
                    placeholder="스터디에 대한 자세한 설명을 입력하세요"
                  />
                  {errors.description && (
                    <p className="mt-1 text-sm text-red-600">
                      {errors.description}
                    </p>
                  )}
                </div>

                <div>
                  <label
                    htmlFor="category"
                    className="block text-sm font-medium text-gray-700"
                  >
                    스터디 분류 *
                  </label>
                  <select
                    id="category"
                    name="category"
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.category ? "border-red-500" : "border-gray-300"
                    }`}
                    value={formData.category}
                    onChange={handleChange}
                  >
                    <option value="">분류를 선택하세요</option>
                    <option value="프로그래밍">프로그래밍</option>
                    <option value="언어">언어</option>
                    <option value="자격증">자격증</option>
                    <option value="취업준비">취업준비</option>
                    <option value="기타">기타</option>
                  </select>
                  {errors.category && (
                    <p className="mt-1 text-sm text-red-600">
                      {errors.category}
                    </p>
                  )}
                </div>

                <div>
                  <label
                    htmlFor="mode"
                    className="block text-sm font-medium text-gray-700"
                  >
                    스터디 모드 *
                  </label>
                  <select
                    id="mode"
                    name="mode"
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.mode ? "border-red-500" : "border-gray-300"
                    }`}
                    value={formData.mode}
                    onChange={handleChange}
                  >
                    <option value="">모드를 선택하세요</option>
                    <option value="온라인">온라인</option>
                    <option value="오프라인">오프라인</option>
                    <option value="하이브리드">하이브리드</option>
                  </select>
                  {errors.mode && (
                    <p className="mt-1 text-sm text-red-600">{errors.mode}</p>
                  )}
                </div>

                <div>
                  <label
                    htmlFor="location"
                    className="block text-sm font-medium text-gray-700"
                  >
                    스터디 위치
                  </label>
                  <input
                    type="text"
                    id="location"
                    name="location"
                    className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                    value={formData.location}
                    onChange={handleChange}
                    placeholder="스터디 장소 (선택사항)"
                  />
                </div>

                <div>
                  <label
                    htmlFor="recruitNumber"
                    className="block text-sm font-medium text-gray-700"
                  >
                    모집 인원 *
                  </label>
                  <input
                    type="number"
                    id="recruitNumber"
                    name="recruitNumber"
                    min="1"
                    max="50"
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.recruitNumber
                        ? "border-red-500"
                        : "border-gray-300"
                    }`}
                    value={formData.recruitNumber}
                    onChange={handleChange}
                  />
                  {errors.recruitNumber && (
                    <p className="mt-1 text-sm text-red-600">
                      {errors.recruitNumber}
                    </p>
                  )}
                </div>

                <div>
                  <label
                    htmlFor="studyStartDate"
                    className="block text-sm font-medium text-gray-700"
                  >
                    스터디 시작일
                  </label>
                  <input
                    type="date"
                    id="studyStartDate"
                    name="studyStartDate"
                    className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                    value={formData.studyStartDate}
                    onChange={handleChange}
                  />
                </div>

                <div>
                  <label
                    htmlFor="studyEndDate"
                    className="block text-sm font-medium text-gray-700"
                  >
                    스터디 종료일
                  </label>
                  <input
                    type="date"
                    id="studyEndDate"
                    name="studyEndDate"
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.studyEndDate ? "border-red-500" : "border-gray-300"
                    }`}
                    value={formData.studyEndDate}
                    onChange={handleChange}
                  />
                  {errors.studyEndDate && (
                    <p className="mt-1 text-sm text-red-600">
                      {errors.studyEndDate}
                    </p>
                  )}
                </div>
              </div>

              {/* Request Form Section */}
              <div className="border-t pt-6">
                <div className="flex items-center justify-between mb-4">
                  <h3 className="text-lg font-medium text-gray-900">
                    지원 양식 설정
                  </h3>
                  <button
                    type="button"
                    onClick={() => setShowRequestForm(!showRequestForm)}
                    className="text-sm text-indigo-600 hover:text-indigo-500"
                  >
                    {showRequestForm ? "양식 숨기기" : "양식 추가하기"}
                  </button>
                </div>

                {showRequestForm && (
                  <div className="bg-gray-50 p-4 rounded-lg">
                    <div className="mb-4">
                      <label
                        htmlFor="formTitle"
                        className="block text-sm font-medium text-gray-700 mb-2"
                      >
                        양식 제목
                      </label>
                      <input
                        type="text"
                        id="formTitle"
                        name="title"
                        className={`block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                          requestFormErrors.title
                            ? "border-red-500"
                            : "border-gray-300"
                        }`}
                        value={requestFormData.title}
                        onChange={handleRequestFormChange}
                        placeholder="지원 양식 제목을 입력하세요"
                      />
                      {requestFormErrors.title && (
                        <p className="mt-1 text-sm text-red-600">
                          {requestFormErrors.title}
                        </p>
                      )}
                    </div>

                    <div className="mb-4">
                      <div className="flex items-center justify-between mb-2">
                        <label className="block text-sm font-medium text-gray-700">
                          질문 목록
                        </label>
                        <button
                          type="button"
                          onClick={addQuestion}
                          className="text-sm bg-indigo-600 text-white px-3 py-1 rounded hover:bg-indigo-700"
                        >
                          질문 추가
                        </button>
                      </div>

                      {requestFormErrors.questions && (
                        <p className="text-sm text-red-600 mb-2">
                          {requestFormErrors.questions}
                        </p>
                      )}

                      {requestFormData.questions.map((question, index) => (
                        <div
                          key={index}
                          className="bg-white p-4 rounded border mb-3"
                        >
                          <div className="flex items-center justify-between mb-2">
                            <span className="text-sm font-medium text-gray-700">
                              질문 {index + 1}
                            </span>
                            <button
                              type="button"
                              onClick={() => removeQuestion(index)}
                              className="text-red-600 hover:text-red-800 text-sm"
                            >
                              삭제
                            </button>
                          </div>

                          <div className="grid grid-cols-1 gap-3">
                            <div>
                              <label className="block text-sm text-gray-600 mb-1">
                                질문 내용
                              </label>
                              <input
                                type="text"
                                className={`block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                                  requestFormErrors[`question_${index}`]
                                    ? "border-red-500"
                                    : "border-gray-300"
                                }`}
                                value={question.questionText}
                                onChange={(e) =>
                                  updateQuestion(
                                    index,
                                    "questionText",
                                    e.target.value,
                                  )
                                }
                                placeholder="질문을 입력하세요"
                              />
                              {requestFormErrors[`question_${index}`] && (
                                <p className="mt-1 text-sm text-red-600">
                                  {requestFormErrors[`question_${index}`]}
                                </p>
                              )}
                            </div>

                            <div className="grid grid-cols-2 gap-3">
                              <div>
                                <label className="block text-sm text-gray-600 mb-1">
                                  답변 유형
                                </label>
                                <select
                                  className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                                  value={question.type}
                                  onChange={(e) =>
                                    updateQuestion(
                                      index,
                                      "type",
                                      e.target.value,
                                    )
                                  }
                                >
                                  <option value="short_answer">단답형</option>
                                  <option value="long_answer">장문형</option>
                                  <option value="checkbox">체크박스</option>
                                  <option value="multiple_choice">
                                    객관식 선택형
                                  </option>
                                </select>
                              </div>

                              <div className="flex items-center">
                                <input
                                  type="checkbox"
                                  id={`required_${index}`}
                                  className="h-4 w-4 text-indigo-600 focus:ring-indigo-500 border-gray-300 rounded"
                                  checked={question.isRequired}
                                  onChange={(e) =>
                                    updateQuestion(
                                      index,
                                      "isRequired",
                                      e.target.checked,
                                    )
                                  }
                                />
                                <label
                                  htmlFor={`required_${index}`}
                                  className="ml-2 block text-sm text-gray-700"
                                >
                                  필수 답변
                                </label>
                              </div>
                            </div>

                            {/* 선택 옵션 관리 (체크박스, 객관식 선택형인 경우) */}
                            {(question.type === "checkbox" ||
                              question.type === "multiple_choice") && (
                              <div className="mt-4">
                                <div className="flex items-center justify-between mb-2">
                                  <label className="block text-sm font-medium text-gray-700">
                                    선택 옵션
                                  </label>
                                  <button
                                    type="button"
                                    onClick={() => addOption(index)}
                                    className="text-sm bg-green-600 text-white px-2 py-1 rounded hover:bg-green-700"
                                  >
                                    옵션 추가
                                  </button>
                                </div>

                                {(!question.options ||
                                  question.options.length === 0) && (
                                  <p className="text-sm text-gray-500 mb-2">
                                    {question.type === "checkbox"
                                      ? "체크박스"
                                      : "객관식"}{" "}
                                    옵션을 추가해주세요.
                                  </p>
                                )}

                                {question.options &&
                                  question.options.map(
                                    (option, optionIndex) => (
                                      <div
                                        key={optionIndex}
                                        className="flex items-center space-x-2 mb-2"
                                      >
                                        <input
                                          type="text"
                                          className="flex-1 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                                          value={option}
                                          onChange={(e) =>
                                            updateOption(
                                              index,
                                              optionIndex,
                                              e.target.value,
                                            )
                                          }
                                          placeholder={`옵션 ${optionIndex + 1}`}
                                        />
                                        <button
                                          type="button"
                                          onClick={() =>
                                            removeOption(index, optionIndex)
                                          }
                                          className="text-red-600 hover:text-red-800 text-sm px-2 py-1"
                                        >
                                          삭제
                                        </button>
                                      </div>
                                    ),
                                  )}

                                {requestFormErrors[
                                  `question_${index}_options`
                                ] && (
                                  <p className="mt-1 text-sm text-red-600">
                                    {
                                      requestFormErrors[
                                        `question_${index}_options`
                                      ]
                                    }
                                  </p>
                                )}
                              </div>
                            )}
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>

              <div className="flex justify-end space-x-4 pt-6">
                <button
                  type="button"
                  onClick={() => navigate("/studies")}
                  className="px-4 py-2 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                >
                  취소
                </button>
                <button
                  type="submit"
                  disabled={loading}
                  className="px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50"
                >
                  {loading ? "생성 중..." : "스터디 생성"}
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

