};

const MyApplications = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [applications, setApplications] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (user) {
      fetchMyApplications();
    }
  }, [user]);

  console.log("MyApplications render - applications:", applications);
  console.log(
    "MyApplications render - applications type:",
    typeof applications,
  );
  console.log(
    "MyApplications render - applications isArray:",
    Array.isArray(applications),
  );
  console.log(
    "MyApplications render - applications keys:",
    applications ? Object.keys(applications) : "no keys",
  );
  console.log("MyApplications render - applications.data:", applications?.data);
  console.log(
    "MyApplications render - applications.data type:",
    typeof applications?.data,
  );
  console.log(
    "MyApplications render - applications.data isArray:",
    Array.isArray(applications?.data),
  );
  console.log(
    "MyApplications render - applications.data length:",
    applications?.data?.length,
  );
  console.log(
    "MyApplications render - applications.data count:",
    applications?.data?.length || 0,
  );
  if (Array.isArray(applications?.data) && applications.data.length > 0) {
    console.log(
      "MyApplications render - first application:",
      applications.data[0],
    );
  }



  const fetchMyApplications = async () => {
    console.log("fetchMyApplications function called");
    try {
      setLoading(true);
      const token = localStorage.getItem("token");

      console.log(
        "Fetching applications with token:",
        token ? "Token exists" : "No token",
      );

      const response = await fetch(
        "http://localhost:3001/api/post/applications",
        {
          headers: { Authorization: `Bearer ${token}` },
        },
      );

      console.log("Response status:", response.status);
      console.log("Response ok:", response.ok);

      if (response.ok) {
        const data = await response.json();
        console.log("Applications data:", data);
        console.log("Applications data.data:", data.data);
        console.log("Applications data.data type:", typeof data.data);
        console.log(
          "Applications data.data isArray:",
          Array.isArray(data.data),
        );
        console.log("Applications data.data length:", data.data?.length);
        console.log("Full response structure:", JSON.stringify(data, null, 2));
        setApplications(data);
      } else {
        const errorText = await response.text();
        console.error("Error response:", errorText);
        setError(`지원 목록을 불러오는데 실패했습니다. (${response.status})`);
      }
    } catch (error) {
      console.error("Failed to fetch my applications:", error);
      setError("지원 목록을 불러오는 중 오류가 발생했습니다.");
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return "미정";
    return new Date(dateString).toLocaleDateString("ko-KR");
  };

  const getStatusBadge = (study) => {
    if (study?.isClosed) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">
          모집 완료
        </span>
      );
    }
    if (study?.currentNumber >= study?.recruitNumber) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">
          인원 마감
        </span>
      );
    }
    return (
      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
        모집 중
      </span>
    );
  };

  const getApplicationStatusBadge = (application) => {
    if (application.isAccepted) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
          수락됨
        </span>
      );
    }
    return (
      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">
        대기중
      </span>
    );
  };

  // user가 null인 경우 로딩 상태 표시
  if (!user) {
    return (
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="flex justify-center">
            <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-indigo-600"></div>
          </div>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
