        )}
      </div>
    </div>
  );
};

const StudyDetail = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const { id } = useParams();
  const [study, setStudy] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [sessions, setSessions] = useState([]);
  const [members, setMembers] = useState([]);
  const [activeTab, setActiveTab] = useState("info");

  console.log("=== StudyDetail Component ===");
  console.log("URL ID from useParams:", id);
  console.log("URL ID type:", typeof id);
  console.log("Current URL:", window.location.href);

  useEffect(() => {
    if (id) {
      fetchStudyDetail();
      fetchStudySessions();
      fetchStudyMembers();
    }
  }, [id]);

  const fetchStudyDetail = async () => {
    try {
      setLoading(true);
      console.log("Fetching study detail for ID:", id);
      console.log("Fetching study detail for ID type:", typeof id);
      console.log("API URL:", `http://localhost:3001/api/post/${id}/detail`);
      const token = localStorage.getItem("token");
      console.log("Token for study detail:", token);

      const response = await fetch(
        `http://localhost:3001/api/post/${id}/detail`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        },
      );

      console.log("Study detail response status:", response.status);

      if (response.ok) {
        const data = await response.json();
        console.log("Study detail data:", data);
        console.log("Study ID from response:", data.data?.id || data.id);
        console.log(
          "Study title from response:",
          data.data?.title || data.title,
        );
        console.log(
          "Study applicationFormTemplate:",
          data.data?.applicationFormTemplate || data.applicationFormTemplate,
        );
        console.log(
          "Study applicationFormTemplate length:",
          (data.data?.applicationFormTemplate || data.applicationFormTemplate)
            ?.length,
        );
        setStudy(data.data || data);
      } else {
        const errorData = await response.json();
        console.error("Study detail error response:", errorData);
        setError("스터디 정보를 불러오는데 실패했습니다.");
      }
    } catch (error) {
      console.error("Fetch study detail error:", error);
      setError("스터디 정보를 불러오는 중 오류가 발생했습니다.");
    } finally {
      setLoading(false);
    }
  };

  const fetchStudySessions = async () => {
    try {
      const response = await fetch(
        `http://localhost:3001/api/post/${id}/sessions`,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        },
      );

      if (response.ok) {
        const data = await response.json();
        setSessions(data.data || data || []);
      }
    } catch (error) {
      console.error("Fetch sessions error:", error);
    }
  };

  const fetchStudyMembers = async () => {
    try {
      const response = await fetch(`http://localhost:3001/api/post/${id}`, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        setMembers(data.data?.studymemberships || data.studymemberships || []);
      }
    } catch (error) {
      console.error("Fetch members error:", error);
    }
  };



  const handleLeaveStudy = async () => {
    try {
      const response = await fetch(
        `http://localhost:3001/api/post/${id}/leave`,
        {
          method: "DELETE",
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        },
      );

      if (response.ok) {
        toast.success("스터디에서 탈퇴했습니다.");
        fetchStudyDetail();
        fetchStudyMembers();
      } else {
        const data = await response.json();
        toast.error(data.message || "스터디 탈퇴에 실패했습니다.");
      }
    } catch (error) {
      console.error("Leave study error:", error);
      toast.error("스터디 탈퇴 중 오류가 발생했습니다.");
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return "미정";
    return new Date(dateString).toLocaleDateString("ko-KR");
  };

  const formatDateTime = (dateString) => {
    if (!dateString) return "미정";
    return new Date(dateString).toLocaleString("ko-KR");
  };

  const getStatusBadge = (study) => {
    if (study?.isClosed) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">
          모집 완료
        </span>
      );
    }
    if (study?.currentNumber >= study?.recruitNumber) {
      return (
        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">
          인원 마감
        </span>
      );
    }
    return (
      <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800">
        모집 중
      </span>
    );
  };

  const isAuthor = study?.author?.id === user?.id;
  const isMember = members.some((member) => member.user?.id === user?.id);

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="flex justify-center">
            <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-indigo-600"></div>
          </div>
        </div>
      </div>
    );
  }

  if (error || !study) {
    return (
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="text-center">
            <h2 className="text-2xl font-bold text-gray-900 mb-4">
              오류가 발생했습니다
            </h2>
            <p className="text-gray-600 mb-4">
              {error || "스터디를 찾을 수 없습니다."}
            </p>
            <button
              onClick={() => navigate("/studies")}
              className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700"
            >
              스터디 목록으로 돌아가기
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        {/* 헤더 */}
        <div className="mb-8">
          <div className="flex items-center justify-between mb-4">
            <button
              onClick={() => navigate("/studies")}
              className="text-indigo-600 hover:text-indigo-800 font-medium"
            >
              ← 스터디 목록으로 돌아가기
            </button>
            <div className="flex space-x-2">
              {isAuthor && (
                <button
                  onClick={() => navigate(`/studies/${id}/edit`)}
                  className="bg-indigo-600 text-white px-4 py-2 rounded-md hover:bg-indigo-700"
                >
                  수정
                </button>
              )}

              {!isAuthor && isMember && (
                <button
                  onClick={handleLeaveStudy}
                  className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700"
                >
                  탈퇴
                </button>
              )}
            </div>
          </div>

          <div className="bg-white rounded-lg shadow-md p-6">
            <div className="flex justify-between items-start mb-4">
              <h1 className="text-3xl font-bold text-gray-900">
                {study.title}
              </h1>
              {getStatusBadge(study)}
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
              <div className="flex items-center text-sm text-gray-500">
                <span className="font-medium mr-2">분류:</span>
                <span className="bg-blue-100 text-blue-800 px-2 py-1 rounded text-xs">
                  {study.category}
                </span>
              </div>
              <div className="flex items-center text-sm text-gray-500">
                <span className="font-medium mr-2">모드:</span>
                <span className="bg-green-100 text-green-800 px-2 py-1 rounded text-xs">
                  {study.mode}
                </span>
              </div>
              {study.location && (
                <div className="flex items-center text-sm text-gray-500">
                  <span className="font-medium mr-2">위치:</span>
                  <span>{study.location}</span>
                </div>
              )}
              <div className="flex items-center text-sm text-gray-500">
                <span className="font-medium mr-2">인원:</span>
                <span>
                  {study.currentNumber || 1}/{study.recruitNumber}명
                </span>
              </div>
              <div className="flex items-center text-sm text-gray-500">
                <span className="font-medium mr-2">작성자:</span>
                <span>{study.author?.username || "알 수 없음"}</span>
              </div>
              <div className="flex items-center text-sm text-gray-500">
                <span className="font-medium mr-2">작성일:</span>
                <span>{formatDate(study.createdAt)}</span>
              </div>
            </div>
          </div>
        </div>

        {/* 탭 네비게이션 */}
        <div className="bg-white rounded-lg shadow-md mb-6">
          <div className="border-b border-gray-200">
            <nav className="-mb-px flex space-x-8 px-6">
              <button
                onClick={() => setActiveTab("info")}
                className={`py-4 px-1 border-b-2 font-medium text-sm ${
                  activeTab === "info"
                    ? "border-indigo-500 text-indigo-600"
                    : "border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300"
                }`}
              >
                스터디 정보
              </button>
              <button
                onClick={() => setActiveTab("sessions")}
                className={`py-4 px-1 border-b-2 font-medium text-sm ${
                  activeTab === "sessions"
                    ? "border-indigo-500 text-indigo-600"
                    : "border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300"
                }`}
              >
                스터디 세션 ({sessions.length})
              </button>
              <button
                onClick={() => setActiveTab("members")}
                className={`py-4 px-1 border-b-2 font-medium text-sm ${
                  activeTab === "members"
                    ? "border-indigo-500 text-indigo-600"
                    : "border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300"
                }`}
              >
                멤버 ({members.length})
              </button>
            </nav>
          </div>

          <div className="p-6">
            {/* 스터디 정보 탭 */}
            {activeTab === "info" && (
              <div>
                <h3 className="text-lg font-medium text-gray-900 mb-4">
                  스터디 설명
                </h3>
                <div className="prose max-w-none">
                  <p className="text-gray-700 whitespace-pre-wrap">
                    {study.description}
                  </p>
                </div>

                {(study.studyStartDate || study.studyEndDate) && (
                  <div className="mt-6">
                    <h4 className="text-md font-medium text-gray-900 mb-2">
                      스터디 기간
                    </h4>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      {study.studyStartDate && (
                        <div>
                          <span className="text-sm font-medium text-gray-500">
                            시작일:
                          </span>
                          <span className="ml-2 text-sm text-gray-900">
                            {formatDate(study.studyStartDate)}
                          </span>
                        </div>
                      )}
                      {study.studyEndDate && (
                        <div>
                          <span className="text-sm font-medium text-gray-500">
                            종료일:
                          </span>
                          <span className="ml-2 text-sm text-gray-900">
                            {formatDate(study.studyEndDate)}
                          </span>
                        </div>
                      )}
                    </div>
                  </div>
                )}
              </div>
            )}

            {/* 스터디 세션 탭 */}
            {activeTab === "sessions" && (
              <div>
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-lg font-medium text-gray-900">
                    스터디 세션
                  </h3>
                  {isAuthor && (
                    <button
                      onClick={() => navigate(`/studies/${id}/sessions/create`)}
                      className="bg-indigo-600 text-white px-3 py-1 rounded-md hover:bg-indigo-700 text-sm"
                    >
                      세션 추가
                    </button>
                  )}
                </div>

                {sessions.length === 0 ? (
                  <p className="text-gray-500">
                    등록된 스터디 세션이 없습니다.
                  </p>
                ) : (
                  <div className="space-y-4">
                    {sessions.map((session) => (
                      <div
                        key={session.id}
                        className="border border-gray-200 rounded-lg p-4"
                      >
                        <div className="flex justify-between items-start">
                          <div>
                            <h4 className="font-medium text-gray-900">
                              {session.title || `${session.sessionNumber}회차`}
                            </h4>
                            {session.description && (
                              <p className="text-sm text-gray-600 mt-1">
                                {session.description}
                              </p>
                            )}
                            <div className="mt-2 text-sm text-gray-500">
                              <span>
                                일시: {formatDateTime(session.scheduledDate)}
                              </span>
                            </div>
                          </div>
                          <span
                            className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                              session.status === "SCHEDULED"
                                ? "bg-blue-100 text-blue-800"
                                : session.status === "IN_PROGRESS"
                                  ? "bg-green-100 text-green-800"
                                  : "bg-gray-100 text-gray-800"
                            }`}
                          >
                            {session.status === "SCHEDULED"
                              ? "예정"
                              : session.status === "IN_PROGRESS"
                                ? "진행중"
                                : "완료"}
                          </span>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}

            {/* 멤버 탭 */}
            {activeTab === "members" && (
              <div>
                <h3 className="text-lg font-medium text-gray-900 mb-4">
                  스터디 멤버
                </h3>

                {members.length === 0 ? (
                  <p className="text-gray-500">등록된 멤버가 없습니다.</p>
                ) : (
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {members.map((member) => (
                      <div
                        key={member.id}
                        className="flex items-center space-x-3 p-3 border border-gray-200 rounded-lg"
                      >
                        <div className="h-10 w-10 bg-indigo-600 rounded-full flex items-center justify-center">
                          <span className="text-white text-sm font-medium">
                            {member.user?.username?.charAt(0).toUpperCase()}
                          </span>
                        </div>
                        <div>
                          <p className="text-sm font-medium text-gray-900">
                            {member.user?.username}
                          </p>
                          <p className="text-xs text-gray-500">
                            {member.user?.email}
                          </p>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
      </div>


    </div>
  );
};

const ProtectedRoute = ({ children }) => {
  const { user, loading } = useAuth();

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  if (!user) {
    return <Navigate to="/login" replace />;
  }

  return <>{children}</>;
};

const Layout = ({ children }) => {
  const { user } = useAuth();
  return (
    <div className="min-h-screen bg-gray-50">
      {user && <Header />}
      <main>{children}</main>
    </div>
  );
};

const App = () => {
  return (
    <QueryClientProvider client={queryClient}>
      <Router>
        <AuthProvider>
          <Layout>
            <Routes>
              <Route
                path="/"
                element={
                  <ProtectedRoute>
                    <Home />
                  </ProtectedRoute>
                }
              />
              <Route path="/login" element={<LoginForm />} />
              <Route path="/register" element={<RegisterForm />} />
              <Route
                path="/me"
                element={
                  <ProtectedRoute>
                    <UserProfile />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/studies"
                element={
                  <ProtectedRoute>
                    <StudyList />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/studies/:id"
                element={
                  <ProtectedRoute>
                    <StudyDetail />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/studies/:id/applicants"
                element={
                  <ProtectedRoute>
                    <StudyApplicants />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/my-studies"
                element={
                  <ProtectedRoute>
                    <MyStudies />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/my-applications"
                element={
                  <ProtectedRoute>
                    <MyApplications />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/my-memberships"
                element={
                  <ProtectedRoute>
                    <MyMemberships />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/my-member-studies"
                element={
                  <ProtectedRoute>
                    <MyMemberStudies />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/my-leader-studies"
                element={
                  <ProtectedRoute>
                    <MyLeaderStudies />
                  </ProtectedRoute>
                }
              />
              <Route
                path="/create-study"
                element={
                  <ProtectedRoute>
                    <CreateStudy />
                  </ProtectedRoute>
                }
              />
              <Route path="*" element={<Navigate to="/" replace />} />
            </Routes>
          </Layout>
          <Toaster position="top-right" />
        </AuthProvider>
      </Router>
    </QueryClientProvider>
  );
};

export default App;
