const CreateStudy = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    category: '',
    mode: '',
    location: '',
    recruitNumber: 1,
    studyStartDate: '',
    studyEndDate: ''
  });
  const [errors, setErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};
    if (!formData.title.trim()) newErrors.title = '스터디 제목을 입력해주세요.';
    if (!formData.description.trim()) newErrors.description = '스터디 내용을 입력해주세요.';
    if (!formData.category) newErrors.category = '스터디 분류를 선택해주세요.';
    if (!formData.mode) newErrors.mode = '스터디 모드를 선택해주세요.';
    if (formData.recruitNumber < 1) newErrors.recruitNumber = '모집 인원은 1명 이상이어야 합니다.';
    if (formData.studyStartDate && formData.studyEndDate && 
        new Date(formData.studyStartDate) > new Date(formData.studyEndDate)) {
      newErrors.studyEndDate = '종료일은 시작일보다 늦어야 합니다.';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    setLoading(true);
    try {
      const response = await fetch('http://localhost:3001/api/post', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          ...formData,
          recruitNumber: parseInt(formData.recruitNumber),
          studyStartDate: formData.studyStartDate || null,
          studyEndDate: formData.studyEndDate || null
        }),
      });

      const data = await response.json();
      
      if (response.ok) {
        toast.success('스터디가 성공적으로 생성되었습니다!');
        navigate('/studies');
      } else {
        toast.error(data.message || '스터디 생성에 실패했습니다.');
      }
    } catch (error) {
      console.error('Create study error:', error);
      toast.error('스터디 생성 중 오류가 발생했습니다.');
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    if (errors[name]) setErrors({ ...errors, [name]: '' });
  };

  return (
    <div className="max-w-4xl mx-auto py-6 sm:px-6 lg:px-8">
      <div className="px-4 py-6 sm:px-0">
        <div className="bg-white shadow rounded-lg">
          <div className="px-4 py-5 sm:p-6">
            <h1 className="text-3xl font-bold text-gray-900 mb-8">새 스터디 만들기</h1>
            
            <form onSubmit={handleSubmit} className="space-y-6">
              <div className="grid grid-cols-1 gap-6 sm:grid-cols-2">
                <div className="sm:col-span-2">
                  <label htmlFor="title" className="block text-sm font-medium text-gray-700">
                    스터디 제목 *
                  </label>
                  <input
                    type="text"
                    id="title"
                    name="title"
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.title ? 'border-red-500' : 'border-gray-300'
                    }`}
                    value={formData.title}
                    onChange={handleChange}
                    placeholder="스터디 제목을 입력하세요"
                  />
                  {errors.title && <p className="mt-1 text-sm text-red-600">{errors.title}</p>}
                </div>

                <div className="sm:col-span-2">
                  <label htmlFor="description" className="block text-sm font-medium text-gray-700">
                    스터디 내용 *
                  </label>
                  <textarea
                    id="description"
                    name="description"
                    rows={4}
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.description ? 'border-red-500' : 'border-gray-300'
                    }`}
                    value={formData.description}
                    onChange={handleChange}
                    placeholder="스터디에 대한 자세한 설명을 입력하세요"
                  />
                  {errors.description && <p className="mt-1 text-sm text-red-600">{errors.description}</p>}
                </div>

                <div>
                  <label htmlFor="category" className="block text-sm font-medium text-gray-700">
                    스터디 분류 *
                  </label>
                  <select
                    id="category"
                    name="category"
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.category ? 'border-red-500' : 'border-gray-300'
                    }`}
                    value={formData.category}
                    onChange={handleChange}
                  >
                    <option value="">분류를 선택하세요</option>
                    <option value="프로그래밍">프로그래밍</option>
                    <option value="언어">언어</option>
                    <option value="자격증">자격증</option>
                    <option value="취업준비">취업준비</option>
                    <option value="기타">기타</option>
                  </select>
                  {errors.category && <p className="mt-1 text-sm text-red-600">{errors.category}</p>}
                </div>

                <div>
                  <label htmlFor="mode" className="block text-sm font-medium text-gray-700">
                    스터디 모드 *
                  </label>
                  <select
                    id="mode"
                    name="mode"
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.mode ? 'border-red-500' : 'border-gray-300'
                    }`}
                    value={formData.mode}
                    onChange={handleChange}
                  >
                    <option value="">모드를 선택하세요</option>
                    <option value="온라인">온라인</option>
                    <option value="오프라인">오프라인</option>
                    <option value="하이브리드">하이브리드</option>
                  </select>
                  {errors.mode && <p className="mt-1 text-sm text-red-600">{errors.mode}</p>}
                </div>

                <div>
                  <label htmlFor="location" className="block text-sm font-medium text-gray-700">
                    스터디 위치
                  </label>
                  <input
                    type="text"
                    id="location"
                    name="location"
                    className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                    value={formData.location}
                    onChange={handleChange}
                    placeholder="스터디 장소 (선택사항)"
                  />
                </div>

                <div>
                  <label htmlFor="recruitNumber" className="block text-sm font-medium text-gray-700">
                    모집 인원 *
                  </label>
                  <input
                    type="number"
                    id="recruitNumber"
                    name="recruitNumber"
                    min="1"
                    max="50"
                    required
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.recruitNumber ? 'border-red-500' : 'border-gray-300'
                    }`}
                    value={formData.recruitNumber}
                    onChange={handleChange}
                  />
                  {errors.recruitNumber && <p className="mt-1 text-sm text-red-600">{errors.recruitNumber}</p>}
                </div>

                <div>
                  <label htmlFor="studyStartDate" className="block text-sm font-medium text-gray-700">
                    스터디 시작일
                  </label>
                  <input
                    type="date"
                    id="studyStartDate"
                    name="studyStartDate"
                    className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500"
                    value={formData.studyStartDate}
                    onChange={handleChange}
                  />
                </div>

                <div>
                  <label htmlFor="studyEndDate" className="block text-sm font-medium text-gray-700">
                    스터디 종료일
                  </label>
                  <input
                    type="date"
                    id="studyEndDate"
                    name="studyEndDate"
                    className={`mt-1 block w-full px-3 py-2 border rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 ${
                      errors.studyEndDate ? 'border-red-500' : 'border-gray-300'
                    }`}
                    value={formData.studyEndDate}
                    onChange={handleChange}
                  />
                  {errors.studyEndDate && <p className="mt-1 text-sm text-red-600">{errors.studyEndDate}</p>}
                </div>
              </div>

              <div className="flex justify-end space-x-4 pt-6">
                <button
                  type="button"
                  onClick={() => navigate('/studies')}
                  className="px-4 py-2 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                >
                  취소
                </button>
                <button
                  type="submit"
                  disabled={loading}
                  className="px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50"
                >
                  {loading ? '생성 중...' : '스터디 생성'}
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

