            <Link to="/" className="text-xl font-bold text-gray-900">StudyGroup</Link>
            <nav className="flex space-x-4">
              <Link to="/" className="text-gray-700 hover:text-gray-900 px-3 py-2 rounded-md text-sm font-medium">홈</Link>
              <Link to="/studies" className="text-gray-700 hover:text-gray-900 px-3 py-2 rounded-md text-sm font-medium">스터디 목록</Link>
              <Link to="/create-study" className="text-gray-700 hover:text-gray-900 px-3 py-2 rounded-md text-sm font-medium">스터디 만들기</Link>
            </nav>
          </div>
          <div className="flex items-center space-x-4">
                         <Link to="/me" className="text-gray-700 hover:text-indigo-600 font-medium">
               안녕하세요, {user?.username || '사용자'}님!
             </Link>
            <button onClick={logout} className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700">
              로그아웃
            </button>
          </div>
        </div>
      </div>
    </header>
  );
};

const Home = () => {
